// MCP client simulator
class MCPClient {
  constructor() {
    this.messageId = 0;
    this.responses = new Map();
  }

  async start() {
    return new Promise((resolve, reject) => {
      const serverPath = join(__dirname, '..', 'dist', 'mcp', 'server.js');

      this.process = spawn('node', [serverPath], {
        stdio: ['pipe', 'pipe', 'pipe'],
      });

      let buffer = '';

      this.process.stdout.on('data', (data) => {
        buffer += data.toString();
        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        lines.forEach((line) => {
          if (line.trim()) {
            try {
              const message = JSON.parse(line);
              if (message.id) {
                const resolver = this.responses.get(message.id);
                if (resolver) {
                  resolver(message);
                  this.responses.delete(message.id);
                }
              }
            } catch (e) {
              // Ignore non-JSON lines (like startup messages)
            }
          }
        });
      });

      this.process.stderr.on('data', (data) => {
        const message = data.toString();
        if (message.includes('running on stdio')) {
          resolve();
        }
      });

      this.process.on('error', reject);

      setTimeout(() => resolve(), 1000); // Fallback timeout
    });
  }

  async callTool(name, args) {
    const id = ++this.messageId;
    const request = {
      jsonrpc: '2.0',
      id,
      method: 'tools/call',
      params: {
        name,
        arguments: args || {},
      },
    };

    return new Promise((resolve, reject) => {
      this.responses.set(id, resolve);
      this.process.stdin.write(JSON.stringify(request) + '\n');
      setTimeout(() => reject(new Error('Timeout')), 5000);
    });
  }

  async readResource(uri) {
    const id = ++this.messageId;
    const request = {
      jsonrpc: '2.0',
      id,
      method: 'resources/read',
      params: { uri },
    };

    return new Promise((resolve, reject) => {
      this.responses.set(id, resolve);
      this.process.stdin.write(JSON.stringify(request) + '\n');
      setTimeout(() => reject(new Error('Timeout')), 5000);
    });
  }

  stop() {
    if (this.process) {
      this.process.kill();
    }
  }
}

// Run tests
async function runTests() {
  logSection('ðŸ§ª Saha UI MCP Server v2.0 Dynamic Features Test Suite');

  const client = new MCPClient();

  try {
    log('Starting MCP server...', 'cyan');
    await client.start();
