 * // With custom icon
 * <Badge icon={<Star size={12} />}>Featured</Badge>
 *
 * // Glass effect with pulse
 * <Badge variant="glass" pulse>Live</Badge>
 *
 * // Accessible badge with aria-label
 * <Badge aria-label="3 unread notifications" variant="error">3</Badge>
 *
 * // Status badge with live region
 * <Badge aria-live="polite" pulse variant="success">Online</Badge>
 * ```
 */
const Badge = React.forwardRef<HTMLSpanElement, BadgeProps>(
  (
    {
      variant = "default",
      size = "md",
      shape = "rounded",
      children,
      className,
      dot = false,
      removable = false,
      onRemove,
      icon,
      pulse = false,
      asChild = false,
      "aria-label": ariaLabel,
      "aria-labelledby": ariaLabelledBy,
      "aria-describedby": ariaDescribedBy,
      "aria-live": ariaLive,
      "aria-atomic": ariaAtomic,
      ...props
    },
    ref,
  ) => {
    const Comp = asChild ? Slot : "span";

    return (
      <Comp
        ref={ref}
        className={cn(
          badgeVariants({ variant, size, shape }),
          pulse && "animate-pulse",
          className,
        )}
        aria-label={ariaLabel}
        aria-labelledby={ariaLabelledBy}
        aria-describedby={ariaDescribedBy}
        aria-live={ariaLive}
        aria-atomic={ariaAtomic}
        {...props}
