import React, { useEffect, useState } from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import type {
  BottomNavigationProps,
  BottomNavigationActionProps,
  BottomNavigationItem,
} from "./BottomNavigation.types";
import {
  bottomNavigationVariants,
  bottomNavigationContainerVariants,
  bottomNavigationActionVariants,
  bottomNavigationIconVariants,
  bottomNavigationLabelVariants,
  bottomNavigationBadgeVariants,
} from "./BottomNavigation.styles";

export type BottomNavigationVariantsProps = VariantProps<
  typeof bottomNavigationVariants
>;

/**
 * BottomNavigationAction Component
 * Represents a single action/item in the bottom navigation
 */
const BottomNavigationAction = React.forwardRef<
  HTMLButtonElement,
  BottomNavigationActionProps
>(
  (
    {
      label,
      icon,
      selected = false,
      showLabel = true,
      badge,
      color = "primary",
      value,
      className,
      disabled = false,
      onClick,
      ...props
    },
    ref,
  ) => {
    const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
      if (!disabled && onClick) {
        onClick(event);
      }
    };

    return (
      <button
        ref={ref}
        className={cn(
          bottomNavigationActionVariants({
            selected,
            showLabel,
            color,
            disabled,
          }),
          className,
        )}
        onClick={handleClick}
        disabled={disabled}
        aria-label={label}
        aria-current={selected ? "page" : undefined}
        type="button"
        {...props}
      >
        {/* Badge */}
        {badge !== undefined && badge !== null && (
          <span
            className={cn(
              bottomNavigationBadgeVariants({
                variant: typeof badge === "boolean" ? "dot" : "standard",
              }),
            )}
          >
            {typeof badge === "boolean" ? "" : badge}
          </span>
        )}

        {/* Icon */}
        <span
          className={cn(
            bottomNavigationIconVariants({
              selected,
              size: "md",
            }),
          )}
        >
          {icon}
        </span>

        {/* Label */}
        <span
          className={cn(
            bottomNavigationLabelVariants({
              selected,
              show: selected || showLabel,
            }),
          )}
        >
          {label}
        </span>
      </button>
    );
  },
);

BottomNavigationAction.displayName = "BottomNavigationAction";

/**
 * BottomNavigation Component
 *
 * A navigation component that appears at the bottom of the screen.
 * Ideal for mobile applications with 3-5 top-level destinations.
 *
 * @example
 * ```tsx
 * // Basic bottom navigation
 * const items = [
 *   { label: 'Home', icon: <Home /> },
 *   { label: 'Search', icon: <Search /> },
 *   { label: 'Profile', icon: <User /> }
 * ];
 * <BottomNavigation items={items} value={0} />
 *
 * // With state management
 * const [active, setActive] = useState(0);
 * <BottomNavigation
 *   items={items}
 *   value={active}
 *   onChange={(value) => setActive(value)}
 * />
 *
 * // With badges
 * const itemsWithBadges = [
 *   { label: 'Home', icon: <Home /> },
 *   { label: 'Messages', icon: <Mail />, badge: 5 },
 *   { label: 'Notifications', icon: <Bell />, badge: '!' }
 * ];
 * <BottomNavigation items={itemsWithBadges} value={active} />
 *
 * // Without labels on inactive items
 * <BottomNavigation
 *   items={items}
 *   value={active}
 *   showLabels={false}
 * />
 *
 * // Different color
 * <BottomNavigation
 *   items={items}
 *   value={active}
 *   color="secondary"
 * />
 *
 * // Shifting variant (animated)
 * <BottomNavigation
 *   variant="shifting"
 *   items={items}
 *   value={active}
 * />
 *
 * // Hide on scroll
 * <BottomNavigation
 *   items={items}
 *   value={active}
 *   hideOnScroll
 * />
 * ```
 */
const BottomNavigation = React.forwardRef<
  HTMLDivElement,
  BottomNavigationProps
>(
  (
    {
      items,
      value,
      variant = "default",
      showLabels = true,
      color = "primary",
      onChange,
      className,
      itemClassName,
      zIndex = 1000,
      hideOnScroll = false,
      elevation = 3,
      bordered = true,
      style,
      hidden: _unusedHidden,
      ...props
    },
    ref,
  ) => {
    const [isHidden, setIsHidden] = useState(false);
    const [prevScrollPos, setPrevScrollPos] = useState(0);

    // Handle hide on scroll
    useEffect(() => {
      if (!hideOnScroll) return;

      const handleScroll = () => {
        const currentScrollPos = window.scrollY;
        const scrollingDown = currentScrollPos > prevScrollPos;
        const scrolledPastThreshold = currentScrollPos > 100;

        // Hide when scrolling down past threshold
        setIsHidden(scrollingDown && scrolledPastThreshold);

        setPrevScrollPos(currentScrollPos);
      };

      window.addEventListener("scroll", handleScroll, { passive: true });

      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }, [hideOnScroll, prevScrollPos]);

    const handleItemClick = (item: BottomNavigationItem, index: number) => {
      if (item.disabled) return;

      const itemValue = item.value !== undefined ? item.value : index;

      // Call item's onClick if provided
      if (item.onClick) {
        item.onClick(itemValue);
      }

      // Call navigation's onChange
      if (onChange) {
        onChange(itemValue);
      }

      // Handle href navigation
      if (item.href) {
        window.location.href = item.href;
      }
    };

    const isSelected = (item: BottomNavigationItem, index: number): boolean => {
      if (value === undefined) return false;

      if (item.value !== undefined) {
        return item.value === value;
      }

      return index === value;
    };

    return (
      <div
        ref={ref}
        className={cn(
          bottomNavigationVariants({
            variant,
            elevation,
            bordered,
            hidden: !!isHidden,
          }),
          className,
        )}
        style={{ zIndex, ...style }}
        role="navigation"
        aria-label="Bottom navigation"
        {...props}
      >
        <div
          className={cn(
            bottomNavigationContainerVariants({
              variant,
            }),
          )}
        >
          {items.map((item, index) => {
            const selected = isSelected(item, index);
            const shouldShowLabel =
              variant === "shifting" ? selected : showLabels || selected;

            return (
              <BottomNavigationAction
                key={item.id || index}
                label={item.label}
                icon={item.icon}
                selected={selected}
                showLabel={shouldShowLabel}
                badge={item.badge}
                color={color}
                value={item.value !== undefined ? item.value : index}
                disabled={item.disabled}
                onClick={() => handleItemClick(item, index)}
                className={itemClassName}
              />
            );
          })}
        </div>
      </div>
    );
  },
);

BottomNavigation.displayName = "BottomNavigation";

export { BottomNavigation, BottomNavigationAction };
export default BottomNavigation;
export type { BottomNavigationProps, BottomNavigationActionProps };

"use client";

import React, { createContext, useContext, useEffect } from "react";
import { cn } from "../../lib/utils";
import type {
  ButtonGroupProps,
  ButtonGroupVariant,
  ButtonGroupOrientation,
  ButtonGroupSize,
} from "./ButtonGroup.types";
import {
  createValidator,
  commonValidators,
  isValidBoolean,
} from "../../lib/validation";
import { buttonGroupVariants } from "./ButtonGroup.styles";

interface ButtonGroupContextValue {
  variant: ButtonGroupVariant;
  size: ButtonGroupSize;
  orientation: ButtonGroupOrientation;
  fullRounded: boolean;
  fullWidth: boolean;
  attached: boolean;
}

const ButtonGroupContext = createContext<ButtonGroupContextValue | undefined>(
  undefined
);

export const useButtonGroup = () => {
  const context = useContext(ButtonGroupContext);
  if (!context) {
    throw new Error("Button must be used within a ButtonGroup");
  }
  return context;
};

const ButtonGroup = React.forwardRef<HTMLDivElement, ButtonGroupProps>(
  (
    {
      variant = "default",
      size = "md",
      orientation = "horizontal",
      fullRounded = false,
      fullWidth = false,
      attached = true,
      children,
      className,
      ...props
    },
    ref
  ) => {
    // Development-only validation
    useEffect(() => {
      const validator = createValidator("ButtonGroup");

      // Validate variant
      validator.validateEnum("variant", variant, [
        "default",
        "outline",
        "ghost",
        "glass",
      ] as const);

      // Validate size
      validator.validateEnum("size", size, ["sm", "md", "lg", "xl"] as const);

      // Validate orientation
      validator.validateEnum("orientation", orientation, [
        "horizontal",
        "vertical",
      ] as const);

      // Validate boolean props
      validator.validateType(
        "fullRounded",
        fullRounded,
        "boolean",
        isValidBoolean
      );
      validator.validateType("fullWidth", fullWidth, "boolean", isValidBoolean);
      validator.validateType("attached", attached, "boolean", isValidBoolean);

      // Validate children
      if (!children) {
        validator.warn("ButtonGroup should have children buttons");
      }

      // Common validators
      commonValidators.className(validator, className);
    }, [
      variant,
      size,
      orientation,
      fullRounded,
      fullWidth,
      attached,
      children,
      className,
    ]);

    const processedChildren = React.Children.map(children, (child, index) => {
      if (!React.isValidElement(child)) return child;

      const isFirst = index === 0;
      const isLast = index === React.Children.count(children) - 1;

      let roundingClasses = "";

      if (attached) {
        if (orientation === "horizontal") {
          if (fullRounded) {
            if (isFirst) roundingClasses = "rounded-l-full rounded-r-none";
            else if (isLast) roundingClasses = "rounded-r-full rounded-l-none";
            else roundingClasses = "rounded-none";
          } else {
            if (isFirst) roundingClasses = "rounded-l-xl rounded-r-none";
            else if (isLast) roundingClasses = "rounded-r-xl rounded-l-none";
            else roundingClasses = "rounded-none";
          }
        } else {
          if (fullRounded) {
            if (isFirst) roundingClasses = "rounded-t-full rounded-b-none";
            else if (isLast) roundingClasses = "rounded-b-full rounded-t-none";
            else roundingClasses = "rounded-none";
          } else {
            if (isFirst) roundingClasses = "rounded-t-xl rounded-b-none";
            else if (isLast) roundingClasses = "rounded-b-xl rounded-t-none";
            else roundingClasses = "rounded-none";
          }
        }

        let borderClasses = "";
        if (variant === "outline" || variant === "ghost") {
          if (orientation === "horizontal" && !isLast) {
            borderClasses = "border-r-0";
          } else if (orientation === "vertical" && !isLast) {
            borderClasses = "border-b-0";
          }
        }

        const zIndexClasses = "hover:z-10 focus:z-10 active:z-10";
        const widthClasses = fullWidth ? "flex-1" : "";

        const childProps = child.props as Record<string, any>;
        return React.cloneElement(child as React.ReactElement<any>, {
          className: cn(
            childProps.className,
            roundingClasses,
            borderClasses,
            zIndexClasses,
            widthClasses
          ),
          size: childProps.size || size,
        });
      }

      const childProps = child.props as Record<string, any>;
      return React.cloneElement(child as React.ReactElement<any>, {
        className: cn(childProps.className, fullWidth && "flex-1"),
        size: childProps.size || size,
      });
    });

    const contextValue: ButtonGroupContextValue = {
      variant,
      size,
      orientation,
      fullRounded,
      fullWidth,
      attached,
    };

    return (
      <ButtonGroupContext.Provider value={contextValue}>
        <div
          ref={ref}
          role="group"
          className={cn(
            buttonGroupVariants({
              variant,
              orientation,
              fullWidth,
              attached,
            }),
            className
          )}
          {...props}
        >
          {processedChildren}
        </div>
      </ButtonGroupContext.Provider>
    );
  }
);

ButtonGroup.displayName = "ButtonGroup";

export { ButtonGroup, buttonGroupVariants };

import React from "react";
import { cn } from "../../lib/utils";
import { ButtonProps } from "./Button.types";
import { buttonVariants, shimmerVariants } from "./Button.styles";
import { Slot } from "../../lib/Slot";

/**
 * Loading spinner component displayed when button is in loading state
 * @private
 */

const LoadingSpinner = () => (
  <svg
    className="animate-spin h-5 w-5"
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
  >
    <circle
      className="opacity-25"
      cx="12"
      cy="12"
      r="10"
      stroke="currentColor"
      strokeWidth="4"
    />
    <path
      className="opacity-75"
      fill="currentColor"
      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
    />
  </svg>
);

/**
 * Button component with multiple variants, sizes, and states
 *
 * @component
 * @example
 * // Basic button
 * <Button>Click me</Button>
 *
 * @example
 * // Button with variant and size
 * <Button variant="primary" size="lg">Large Primary</Button>
 *
 * @example
 * // Loading button
 * <Button loading>Saving...</Button>
 *
 * @example
 * // Button as link (asChild pattern)
 * <Button asChild>
 *   <a href="/home">Home</a>
 * </Button>
 *
 * @example
 * // Accessible button with icon
 * <Button aria-label="Close dialog">
 *   <XIcon />
 * </Button>
 */
const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      variant = "primary",
      size = "md",
      className,
      children,
      disabled,
      asChild = false,
      loading,
      "aria-label": ariaLabel,
      "aria-labelledby": ariaLabelledBy,
      "aria-describedby": ariaDescribedBy,
      "aria-pressed": ariaPressed,
      "aria-expanded": ariaExpanded,
      "aria-controls": ariaControls,
      "aria-haspopup": ariaHasPopup,
      ...props
    },
    ref,
  ) => {
    const hasGlow = !["outline", "ghost", "glass"].includes(variant);
    const Comp = asChild ? Slot : "button";

    return (
      <Comp
        ref={ref}
        className={cn(
          buttonVariants({ variant, size }),
          shimmerVariants({ variant }),
          className,
        )}
        disabled={disabled || loading}
        aria-busy={loading ? "true" : undefined}
        aria-label={ariaLabel}
        aria-labelledby={ariaLabelledBy}
        aria-describedby={ariaDescribedBy}
        aria-pressed={ariaPressed}
        aria-expanded={ariaExpanded}
        aria-controls={ariaControls}
        aria-haspopup={ariaHasPopup}
        aria-disabled={disabled || loading ? "true" : undefined}
        {...props}
      >
        {asChild ? (
          children
        ) : (
          <>
            {/* Ripple effect container */}
            <span className="absolute inset-0 rounded-inherit overflow-hidden">
              <span className="absolute inset-0 bg-white/0 group-active:bg-white/20 transition-colors duration-150 rounded-inherit"></span>
            </span>

            {/* Content */}
            <span className="relative z-10 flex items-center justify-center gap-inherit font-semibold tracking-wide">
              {loading && <LoadingSpinner />}
              {children}
            </span>

            {/* Glow effect on hover for colored variants */}
            {hasGlow && (
              <span className="absolute -inset-1 bg-gradient-to-r from-transparent via-current to-transparent opacity-0 group-hover:opacity-20 blur-xl transition-opacity duration-500 -z-10"></span>
            )}
          </>
        )}
      </Comp>
    );
  },
);

Button.displayName = "Button";

export default Button;
export { buttonVariants };

