"use client";

import {
  ResponsiveContainer,
  ComposedChart,
  Bar,
  Line,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from "recharts";
import { ChartTooltipContent } from "../components/ChartTooltip";
import { useChartColors } from "../../../hooks/useChartColors";
import type {
  BaseChartComponentProps,
  BarSeriesConfig,
  LineSeriesConfig,
  AreaSeriesConfig,
} from "../Chart.types";

export function ComposedChartComponent({
  config,
  variant,
  size,
  hiddenSeries,
}: BaseChartComponentProps) {
  const { getColor } = useChartColors(variant);

  const visibleSeries = config.series.filter(
    (s) => !hiddenSeries.has(s.dataKey) && !s.hide,
  );

  const heightMap = {
    sm: 200,
    md: 300,
    lg: 400,
    xl: 500,
  };

  const renderSeries = (series: any, index: number) => {
    const color = series.color || getColor(index);
    const seriesType = (series as any).type || "bar";

    switch (seriesType) {
      case "line":
        const lineSeries = series as LineSeriesConfig;
        return (
          <Line
            key={series.dataKey}
            type={lineSeries.type || "monotone"}
            dataKey={series.dataKey}
            name={series.name || series.dataKey}
            stroke={color}
            strokeWidth={lineSeries.strokeWidth || 2}
            dot={lineSeries.dot !== false ? { r: 3, strokeWidth: 2 } : false}
            activeDot={lineSeries.activeDot !== false ? { r: 4 } : false}
          />
        );

      case "area":
        const areaSeries = series as AreaSeriesConfig;
        return (
          <Area
            key={series.dataKey}
            type={areaSeries.type || "monotone"}
            dataKey={series.dataKey}
            name={series.name || series.dataKey}
            stroke={color}
            fill={color}
            strokeWidth={areaSeries.strokeWidth || 2}
            fillOpacity={areaSeries.fillOpacity ?? 0.6}
            stackId={areaSeries.stackId}
          />
        );

      case "bar":
      default:
        const barSeries = series as BarSeriesConfig;
        return (
          <Bar
            key={series.dataKey}
            dataKey={series.dataKey}
            name={series.name || series.dataKey}
            fill={color}
            stackId={barSeries.stackId}
            radius={barSeries.radius || [4, 4, 0, 0]}
            maxBarSize={barSeries.barSize || 60}
          />
        );
    }
  };

  return (
    <ResponsiveContainer width="100%" height={heightMap[size]}>
      <ComposedChart
        data={config.data}
        margin={{ top: 5, right: 10, left: 10, bottom: 0 }}
        accessibilityLayer
      >
        {config.grid?.show !== false && (
          <CartesianGrid
            strokeDasharray="3 3"
            vertical={config.grid?.vertical ?? false}
            horizontal={config.grid?.horizontal ?? true}
          />
        )}

        <XAxis
          dataKey={config.xAxis?.dataKey}
          hide={config.xAxis?.hide}
          tickLine={false}
          axisLine={false}
          tickMargin={8}
          tickFormatter={config.xAxis?.tickFormatter}
          label={
            config.xAxis?.label
              ? {
                  value: config.xAxis.label,
                  position: "insideBottom",
                  offset: -5,
                }
              : undefined
          }
        />

        <YAxis
          hide={config.yAxis?.hide}
          tickLine={false}
          axisLine={false}
          tickMargin={8}
          tickFormatter={config.yAxis?.tickFormatter}
          domain={config.yAxis?.domain}
          scale={config.yAxis?.scale}
          label={
            config.yAxis?.label
              ? {
                  value: config.yAxis.label,
                  angle: -90,
                  position: "insideLeft",
                }
              : undefined
          }
        />

        {config.tooltip?.show !== false && (
          <Tooltip content={<ChartTooltipContent />} />
        )}

        {visibleSeries.map((series, index) => renderSeries(series, index))}
      </ComposedChart>
    </ResponsiveContainer>
  );
}

"use client";

// Main component
export { Chart } from "./Chart";

// Core Shadcn-style components
export { ChartContainer } from "./components/ChartContainer";
export { ChartWrapper } from "./components/ChartWrapper";
export { ChartHeader } from "./components/ChartHeader";
export { ChartTooltip, ChartTooltipContent } from "./components/ChartTooltip";
export { ChartLegend, ChartLegendContent } from "./components/ChartLegend";
export { ChartLoading } from "./components/ChartLoading";

// Chart type components (backward compatibility)
export { LineChartComponent } from "./charts/LineChartComponent";
export { BarChartComponent } from "./charts/BarChartComponent";
export { AreaChartComponent } from "./charts/AreaChartComponent";
export { PieChartComponent } from "./charts/PieChartComponent";
export { RadarChartComponent } from "./charts/RadarChartComponent";
export { RadialBarChartComponent } from "./charts/RadialBarChartComponent";
export { ScatterChartComponent } from "./charts/ScatterChartComponent";
export { ComposedChartComponent } from "./charts/ComposedChartComponent";
export { FunnelChartComponent } from "./charts/FunnelChartComponent";
export { TreemapChartComponent } from "./charts/TreemapChartComponent";

// Hooks
export { useChartColors } from "../../hooks/useChartColors";
export { useChartData } from "../../hooks/useChartData";

// Utils
export { formatters } from "../../lib/formatters";
export { createChartConfig } from "../../lib/chartConfig";

// Types
export type * from "./Chart.types";
export type {
  ChartConfig,
  ChartContainerProps,
} from "./components/ChartContainer";
export type { ChartWrapperProps } from "./components/ChartWrapper";
export type { ChartTooltipProps } from "./components/ChartTooltip";
export type { ChartLegendProps } from "./components/ChartLegend";
export type { ChartHeaderProps } from "./components/ChartHeader";

// Styles
export * from "./Chart.styles";

"use client";

import React, {
  createContext,
  useContext,
  useState,
  useCallback,
  useEffect,
  useRef,
} from "react";
import { ChevronsUpDown } from "lucide-react";
import { cn } from "../../lib/utils";
import { ComponentValidator, isValidBoolean } from "../../lib/validation";
import type {
  CollapsibleProps,
  CollapsibleTriggerProps,
  CollapsibleContentProps,
  CollapsibleCompactProps,
  CollapsibleContextValue,
  CollapsibleVariant,
  CollapsibleAnimation,
} from "./Collapsible.types";
import {
  collapsibleVariants,
  collapsibleTriggerVariants,
  collapsibleContentVariants,
  collapsibleIconVariants,
} from "./Collapsible.styles";

// ===========================
// Validation Constants
// ===========================

const VALID_VARIANTS: CollapsibleVariant[] = [
  "default",
  "primary",
  "secondary",
  "accent",
  "success",
  "warning",
  "error",
  "info",
  "outline",
  "glass",
];

const VALID_ANIMATIONS: CollapsibleAnimation[] = [
  "smooth",
  "spring",
  "bounce",
  "none",
];

// ===========================
// Context
// ===========================

const CollapsibleContext = createContext<CollapsibleContextValue | undefined>(
  undefined
);

const useCollapsibleContext = () => {
  const context = useContext(CollapsibleContext);
  if (!context) {
    throw new Error(
      "Collapsible compound components must be used within a Collapsible component"
    );
  }
  return context;
};

// ===========================
// Collapsible Component
// ===========================

/**
 * Collapsible - A component that can expand and collapse content
 *
 * Features:
 * - Controlled and uncontrolled modes
 * - 10 visual variants (including glass)
 * - Multiple animation styles
 * - Keyboard accessible
 * - Customizable trigger and content
 * - Compile-time and runtime validation
 *
 * @example
 * ```tsx
 * // Basic usage
 * <Collapsible>
 *   <CollapsibleTrigger>Click to expand</CollapsibleTrigger>
 *   <CollapsibleContent>Hidden content here</CollapsibleContent>
 * </Collapsible>
 *
 * // Controlled
 * <Collapsible open={isOpen} onOpenChange={setIsOpen} variant="primary">
 *   <CollapsibleTrigger>Toggle</CollapsibleTrigger>
 *   <CollapsibleContent>Content</CollapsibleContent>
 * </Collapsible>
 *
 * // With custom trigger
 * <Collapsible variant="glass">
 *   <CollapsibleTrigger asChild>
 *     <Button>Custom Trigger</Button>
 *   </CollapsibleTrigger>
 *   <CollapsibleContent>Content</CollapsibleContent>
 * </Collapsible>
 * ```
 */
const Collapsible = React.forwardRef<HTMLDivElement, CollapsibleProps>(
  (
    {
      children,
      open: controlledOpen,
      defaultOpen = false,
      onOpenChange,
      variant = "default",
      animation = "smooth",
      disabled = false,
      className,
      onAnimationStart,
      onAnimationEnd,
      preserveContent = false,
      animationDelay = 0,
      animationDuration = 500,
      ...props
    },
    ref
  ) => {
    // Runtime validation
    const validator = new ComponentValidator("Collapsible");

    React.useEffect(() => {
      validator.validateEnum("variant", variant, VALID_VARIANTS);
      validator.validateEnum("animation", animation, VALID_ANIMATIONS);
      if (controlledOpen !== undefined) {
        validator.validateType(
          "open",
          controlledOpen,
          "boolean",
          isValidBoolean
        );
      }
      if (defaultOpen !== undefined) {
        validator.validateType(
          "defaultOpen",
          defaultOpen,
          "boolean",
          isValidBoolean
        );
      }
      validator.validateType("disabled", disabled, "boolean", isValidBoolean);
      if (animationDuration !== undefined) {
        validator.validateType(
          "animationDuration",
          animationDuration,
          "number",
          (val: any) => typeof val === "number" && val >= 0
        );
      }
    }, [
      variant,
      animation,
      controlledOpen,
      defaultOpen,
      disabled,
      animationDuration,
    ]);

    // State management
    const [internalOpen, setInternalOpen] = useState(defaultOpen);
    const isControlled = controlledOpen !== undefined;
    const open = isControlled ? controlledOpen : internalOpen;

    const handleOpenChange = useCallback(
      (newOpen: boolean) => {
        if (disabled) return;

        if (!isControlled) {
          setInternalOpen(newOpen);
        }

        onOpenChange?.(newOpen);
      },
      [disabled, isControlled, onOpenChange]
    );

    const contextValue: CollapsibleContextValue = {
      open,
      setOpen: handleOpenChange,
      disabled,
      variant,
      animation,
      animationDuration,
    };

    return (
      <CollapsibleContext.Provider value={contextValue}>
        <div
          ref={ref}
          className={cn(collapsibleVariants({ variant, disabled }), className)}
          {...props}
        >
          {children}
        </div>
      </CollapsibleContext.Provider>
    );
  }
);

Collapsible.displayName = "Collapsible";

// ===========================
// CollapsibleTrigger Component
// ===========================

/**
 * CollapsibleTrigger - Clickable element that toggles the collapsible
 *
 * @example
 * ```tsx
 * <CollapsibleTrigger>Click me</CollapsibleTrigger>
 *
 * // With custom element
 * <CollapsibleTrigger asChild>
 *   <Button variant="outline">Toggle</Button>
 * </CollapsibleTrigger>
 *
 * // Without icon
 * <CollapsibleTrigger showIcon={false}>
 *   Custom trigger
 * </CollapsibleTrigger>
 * ```
 */
const CollapsibleTrigger = React.forwardRef<
  HTMLButtonElement,
  CollapsibleTriggerProps
>(
  (
    {
      asChild = false,
      children,
      className,
      showIcon = true,
      icon,
      iconPosition = "right",
      onClick,
      ...props
    },
    ref
  ) => {
    const { open, setOpen, disabled, variant } = useCollapsibleContext();

    const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      if (disabled) return;
      setOpen(!open);
      onClick?.(e);
    };

    // Use ChevronsUpDown icon - shows both arrows, no rotation needed
    const iconElement = icon || <ChevronsUpDown className="h-4 w-4" />;
    const iconWithStyles = (
      <span className={cn(collapsibleIconVariants({ open, variant }))}>
        {iconElement}
      </span>
    );

    // If asChild, merge props into child
    if (asChild && React.isValidElement(children)) {
      const child = children as React.ReactElement<any>;
      return React.cloneElement(child, {
        ...child.props,
        onClick: (e: React.MouseEvent<HTMLButtonElement>) => {
          child.props.onClick?.(e);
          handleClick(e);
        },
        disabled: disabled || child.props.disabled,
        "aria-expanded": open,
        "aria-controls": "collapsible-content",
        ...props,
      });
    }

    return (
      <button
        ref={ref}
        type="button"
        onClick={handleClick}
        disabled={disabled}
        aria-expanded={open}
        aria-controls="collapsible-content"
        className={cn(collapsibleTriggerVariants({ variant, open }), className)}
        {...props}
      >
        {showIcon && iconPosition === "left" && iconWithStyles}
        <span className="flex-1">{children}</span>
        {showIcon && iconPosition === "right" && iconWithStyles}
      </button>
    );
  }
);

CollapsibleTrigger.displayName = "CollapsibleTrigger";

// ===========================
// CollapsibleContent Component
// ===========================

/**
 * CollapsibleContent - Content that expands/collapses
 *
 * @example
 * ```tsx
 * <CollapsibleContent>
 *   <div className="p-4">
 *     Your content here
 *   </div>
 * </CollapsibleContent>
 *
 * // Force mount (keep in DOM)
 * <CollapsibleContent forceMount>
 *   Content that stays in DOM
 * </CollapsibleContent>
 * ```
 */
const CollapsibleContent = React.forwardRef<
  HTMLDivElement,
  CollapsibleContentProps
>(({ children, className, forceMount = false, ...props }, ref) => {
  const { open, variant, animation, animationDuration } =
    useCollapsibleContext();
  const contentRef = useRef<HTMLDivElement>(null);
  const [height, setHeight] = useState<number | string>(open ? "auto" : 0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (!contentRef.current) return;

    if (open) {
      setIsAnimating(true);
      // Get the height before animation
      const contentHeight = contentRef.current.scrollHeight;
      setHeight(contentHeight);

      // Set to auto after animation completes
      const timer = setTimeout(() => {
        setHeight("auto");
        setIsAnimating(false);
      }, animationDuration);

      return () => clearTimeout(timer);
    } else {
      setIsAnimating(true);

      // First, ensure we have an explicit height
      if (height === "auto") {
        const currentHeight = contentRef.current.scrollHeight;
        setHeight(currentHeight);
      }

      // Then animate to 0 after the browser has rendered with explicit height
      const timer = setTimeout(() => {
        setHeight(0);
      }, 10); // Small delay to ensure the browser has painted

      const animTimer = setTimeout(() => {
        setIsAnimating(false);
      }, animationDuration + 10);

      return () => {
        clearTimeout(timer);
        clearTimeout(animTimer);
      };
    }
  }, [open, animationDuration, height]);

  // Render with height 0 when closed instead of not rendering at all
  // This allows smooth animation on first open
  const shouldRender = open || forceMount || isAnimating;

  // Get transition timing function based on animation type
  const getTimingFunction = () => {
    switch (animation) {
      case "smooth":
        return "ease-in-out";
      case "spring":
        return "cubic-bezier(0.68, -0.55, 0.265, 1.55)";
      case "bounce":
        return "cubic-bezier(0.68, -0.6, 0.32, 1.6)";
      case "none":
        return "linear";
      default:
        return "ease-in-out";
    }
  };

  return (
    <div
      ref={ref}
      id="collapsible-content"
      data-state={open ? "open" : "closed"}
      style={{
        height: shouldRender ? height : 0,
        overflow: "hidden",
        transition:
          animation === "none"
            ? "none"
            : `height ${animationDuration}ms ${getTimingFunction()}`,
      }}
      className={cn(
        collapsibleContentVariants({ variant, animation }),
        !shouldRender && "hidden",
        className
      )}
      {...props}
    >
      <div ref={contentRef}>{children}</div>
    </div>
  );
});

CollapsibleContent.displayName = "CollapsibleContent";

// ===========================
// Compact API Component
// ===========================

/**
 * Collapsible.Compact - Single component with all props (no composition)
 *
 * @example
 * ```tsx
 * <Collapsible.Compact
 *   title="Click to expand"
 *   content="Hidden content"
 *   variant="primary"
 *   defaultOpen
 * />
 *
 * <Collapsible.Compact
 *   title={<h3>Custom Title</h3>}
 *   content={<div className="p-4">Complex content</div>}
 *   showIcon={false}
 *   variant="glass"
 * />
 * ```
 */
const CollapsibleCompact = React.forwardRef<
  HTMLDivElement,
  CollapsibleCompactProps
>(
  (
    {
      title,
      content,
      open,
      defaultOpen,
      onOpenChange,
      variant = "default",
      animation = "smooth",
      disabled = false,
      className,
      triggerClassName,
      contentClassName,
      showIcon = true,
      iconPosition = "right",
      icon,
      animationDuration = 500,
      ...props
    },
    ref
  ) => {
    return (
      <Collapsible
        ref={ref}
        open={open}
        defaultOpen={defaultOpen}
        onOpenChange={onOpenChange}
        variant={variant}
        animation={animation}
        disabled={disabled}
        className={className}
        animationDuration={animationDuration}
        {...props}
      >
        <CollapsibleTrigger
          className={triggerClassName}
          showIcon={showIcon}
          icon={icon}
          iconPosition={iconPosition}
        >
          {title}
        </CollapsibleTrigger>
        <CollapsibleContent className={contentClassName}>
          {content}
        </CollapsibleContent>
      </Collapsible>
    );
  }
);

CollapsibleCompact.displayName = "Collapsible.Compact";

// ===========================
// Exports
// ===========================

export {
  Collapsible,
  CollapsibleTrigger,
  CollapsibleContent,
  CollapsibleCompact,
  CollapsibleContext,
  useCollapsibleContext,
};
export type {
  CollapsibleProps,
  CollapsibleTriggerProps,
  CollapsibleContentProps,
  CollapsibleCompactProps,
  CollapsibleVariant,
  CollapsibleAnimation,
};

"use client";

import React, { useState, useRef, useEffect } from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import { X } from "lucide-react";
import type { ColorPickerProps, PresetColor } from "./ColorPicker.types";
import {
  colorPickerVariants,
  colorPickerTriggerVariants,
  colorPickerSwatchVariants,
  colorPickerPopoverVariants,
  colorPickerInputVariants,
  colorPickerPresetColorsVariants,
  colorPickerPresetColorVariants,
  colorPickerClearButtonVariants,
} from "./ColorPicker.styles";

export type ColorPickerVariantsProps = VariantProps<typeof colorPickerVariants>;

// Default preset colors
const DEFAULT_PRESETS: PresetColor[] = [
  { color: "#f5222d", label: "Red" },
  { color: "#fa541c", label: "Orange" },
  { color: "#fa8c16", label: "Gold" },
  { color: "#faad14", label: "Yellow" },
  { color: "#52c41a", label: "Green" },
  { color: "#13c2c2", label: "Cyan" },
  { color: "#1890ff", label: "Blue" },
  { color: "#2f54eb", label: "Purple" },
  { color: "#722ed1", label: "Violet" },
  { color: "#eb2f96", label: "Magenta" },
  { color: "#000000", label: "Black" },
  { color: "#ffffff", label: "White" },
  { color: "#f0f0f0", label: "Gray 1" },
  { color: "#d9d9d9", label: "Gray 2" },
  { color: "#bfbfbf", label: "Gray 3" },
  { color: "#8c8c8c", label: "Gray 4" },
];

/**
 * ColorPicker Component
 *
 * A color picker component with preset colors and hex input.
 * Perfect for theme customization and design tools.
 * Supports hex color format with optional alpha channel.
 *
 * @example
 * ```tsx
 * // Basic color picker
 * <ColorPicker
 *   value={color}
 *   onChange={setColor}
 * />
 *
 * // With presets
 * <ColorPicker
 *   value={color}
 *   onChange={setColor}
 *   presets={customPresets}
 * />
 *
 * // With clear button
 * <ColorPicker
 *   value={color}
 *   onChange={setColor}
 *   allowClear
 * />
 * ```
 */
const ColorPicker = React.forwardRef<HTMLDivElement, ColorPickerProps>(
  (
    {
      value: controlledValue,
      defaultValue = "#000000",
      onChange,
      format = "hex",
      showFormatSwitch = false,
      disableAlpha = false,
      presets = DEFAULT_PRESETS,
      showPresets = true,
      size = "md",
      disabled = false,
      placement = "bottom-start",
      trigger,
      allowClear = false,
      className,
      triggerClassName,
      popoverClassName,
      asChild = false,
      ...props
    },
    ref,
  ) => {
    const [uncontrolledValue, setUncontrolledValue] = useState(defaultValue);
    const [isOpen, setIsOpen] = useState(false);
    const [inputValue, setInputValue] = useState("");
    const containerRef = useRef<HTMLDivElement>(null);
    const popoverRef = useRef<HTMLDivElement>(null);

    // Determine if component is controlled
    const isControlled = controlledValue !== undefined;
    const currentColor = isControlled ? controlledValue : uncontrolledValue;

    // Update input value when color changes
    useEffect(() => {
      setInputValue(currentColor || "");
    }, [currentColor]);

    // Handle color change
    const handleColorChange = (newColor: string) => {
      if (disabled) return;

      if (!isControlled) {
        setUncontrolledValue(newColor);
      }
      onChange?.(newColor);
    };

    // Handle input change
    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const value = e.target.value;
      setInputValue(value);

      // Validate hex color
      if (/^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$/.test(value)) {
        handleColorChange(value);
      }
    };

    // Handle preset click
    const handlePresetClick = (color: string) => {
      handleColorChange(color);
      setIsOpen(false);
    };

    // Handle clear
    const handleClear = (e: React.MouseEvent) => {
      e.stopPropagation();
      handleColorChange("");
    };

    // Handle click outside
    useEffect(() => {
      if (!isOpen) return;

      const handleClickOutside = (e: MouseEvent) => {
        if (
          containerRef.current &&
          !containerRef.current.contains(e.target as Node) &&
          popoverRef.current &&
          !popoverRef.current.contains(e.target as Node)
        ) {
          setIsOpen(false);
        }
      };

      document.addEventListener("mousedown", handleClickOutside);
      return () => document.removeEventListener("mousedown", handleClickOutside);
    }, [isOpen]);

    // Handle escape key
    useEffect(() => {
      if (!isOpen) return;

      const handleEscape = (e: KeyboardEvent) => {
        if (e.key === "Escape") {
          setIsOpen(false);
        }
      };

      document.addEventListener("keydown", handleEscape);
      return () => document.removeEventListener("keydown", handleEscape);
    }, [isOpen]);

    const normalizedPresets = Array.isArray(presets[0])
      ? (presets as PresetColor[][])
      : [presets as PresetColor[]];

    return (
      <div
        ref={(node) => {
          if (typeof ref === "function") {
            ref(node);
          } else if (ref) {
            ref.current = node;
          }
          (containerRef as React.MutableRefObject<HTMLDivElement | null>).current = node;
        }}
        className={cn(colorPickerVariants({ size, disabled }), className)}
        {...props}
      >
        {/* Trigger */}
        {trigger ? (
          <div onClick={() => !disabled && setIsOpen(!isOpen)}>{trigger}</div>
        ) : (
          <button
            type="button"
            onClick={() => !disabled && setIsOpen(!isOpen)}
            disabled={disabled}
            className={cn(
              colorPickerTriggerVariants({ size, disabled }),
              triggerClassName,
            )}
            aria-label="Pick a color"
            aria-expanded={isOpen}
          >
            <div
              className={cn(colorPickerSwatchVariants({ size }))}
              style={{
                background: currentColor || "#ffffff",
              }}
            >
              {!currentColor && (
                <div
                  className="absolute inset-0"
                  style={{
                    background:
                      "linear-gradient(45deg, #ccc 25%, transparent 25%), linear-gradient(-45deg, #ccc 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #ccc 75%), linear-gradient(-45deg, transparent 75%, #ccc 75%)",
                    backgroundSize: "8px 8px",
                    backgroundPosition: "0 0, 0 4px, 4px -4px, -4px 0px",
                  }}
                />
              )}
            </div>
            <span className="text-sm">{currentColor || "Select"}</span>
            {allowClear && currentColor && (
              <button
                type="button"
                onClick={handleClear}
                className={cn(colorPickerClearButtonVariants())}
                aria-label="Clear color"
              >
                <X className="h-3 w-3" />
              </button>
            )}
          </button>
        )}

        {/* Popover */}
        {isOpen && (
          <div
            ref={popoverRef}
            className={cn(
              colorPickerPopoverVariants({ placement }),
              "absolute z-50 mt-2",
              popoverClassName,
            )}
            role="dialog"
            aria-label="Color picker"
          >
            {/* Hex Input */}
            <div className="mb-3">
              <label className="block text-xs font-medium text-muted-foreground mb-1">
                Hex Color
              </label>
              <input
                type="text"
                value={inputValue}
                onChange={handleInputChange}
                placeholder="#000000"
                className={cn(colorPickerInputVariants({ disabled }))}
                disabled={disabled}
              />
            </div>

            {/* Preset Colors */}
            {showPresets && normalizedPresets.length > 0 && (
              <div className="space-y-3">
                {normalizedPresets.map((group, groupIndex) => (
                  <div key={groupIndex}>
                    <div className={cn(colorPickerPresetColorsVariants())}>
                      {group.map((preset) => (
                        <button
                          key={preset.color}
                          type="button"
                          onClick={() => handlePresetClick(preset.color)}
                          className={cn(
                            colorPickerPresetColorVariants({
                              active: preset.color === currentColor,
                            }),
                          )}
                          style={{ background: preset.color }}
                          title={preset.label}
                          aria-label={`Select ${preset.label || preset.color}`}
                        >
                          {preset.color === currentColor && (
                            <div className="absolute inset-0 flex items-center justify-center">
                              <div className="w-2 h-2 rounded-full bg-white shadow" />
                            </div>
                          )}
                        </button>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    );
  },
);

ColorPicker.displayName = "ColorPicker";

export { ColorPicker };
export default ColorPicker;
export type { ColorPickerProps, PresetColor };

