"use client";

import {
  createContext,
  useContext,
  forwardRef,
  isValidElement,
  cloneElement,
} from "react";
import { X } from "lucide-react";
import { cn } from "../../lib/utils";
import type { DrawerContextValue } from "./Drawer.types";
import type {
  DrawerTriggerProps,
  DrawerHeaderProps,
  DrawerBodyProps,
  DrawerFooterProps,
  DrawerTitleProps,
  DrawerDescriptionProps,
  DrawerCloseProps,
  DrawerCloseButtonProps,
} from "./Drawer.subcomponents.types";

/**
 * Drawer Context
 */
export const DrawerContext = createContext<DrawerContextValue | null>(null);

/**
 * Hook to use drawer context
 */
export const useDrawerContext = () => {
  const context = useContext(DrawerContext);
  if (!context) {
    throw new Error("Drawer components must be used within a Drawer");
  }
  return context;
};

/**
 * DrawerTrigger - Button to open the drawer
 */
export const DrawerTrigger = forwardRef<HTMLButtonElement, DrawerTriggerProps>(
  ({ children, className, onClick, asChild = false, ...props }, ref) => {
    const { setOpen } = useDrawerContext();

    const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      setOpen(true);
      onClick?.(e);
    };

    // If asChild is true, clone the child element and pass onClick to it
    if (asChild && isValidElement(children)) {
      const childProps = children.props as any;
      return cloneElement(children, {
        ...childProps,
        ref,
        onClick: (e: React.MouseEvent<HTMLButtonElement>) => {
          handleClick(e);
          // Call the child's original onClick if it exists
          if (childProps.onClick) {
            childProps.onClick(e);
          }
        },
      } as any);
    }

    return (
      <button
        ref={ref}
        type="button"
        onClick={handleClick}
        className={className}
        {...props}
      >
        {children}
      </button>
    );
  }
);

