"use client";

// Main Drawer component
export { Drawer, default } from "./Drawer";

// Sub-components
export {
  DrawerTrigger,
  DrawerHeader,
  DrawerBody,
  DrawerFooter,
  DrawerTitle,
  DrawerDescription,
  DrawerClose,
  DrawerCloseButton,
} from "./DrawerComponents";

// Overlay and Content
export { DrawerOverlay, DrawerContent } from "./DrawerOverlay";

// Types
export type { DrawerProps, DrawerContextValue } from "./Drawer.types";
export type {
  DrawerTriggerProps,
  DrawerContentProps,
  DrawerOverlayProps,
  DrawerHeaderProps,
  DrawerBodyProps,
  DrawerFooterProps,
  DrawerTitleProps,
  DrawerDescriptionProps,
  DrawerCloseProps,
  DrawerCloseButtonProps,
} from "./Drawer.subcomponents.types";

"use client";

import {
  forwardRef,
  createContext,
  useContext,
  useState,
  useEffect,
  useRef,
  isValidElement,
  cloneElement,
} from "react";
import { cn } from "../../lib/utils";
import { useClickOutside } from "../../hooks/useClickOutside";
import type {
  DropdownProps,
  DropdownTriggerProps,
  DropdownContentProps,
  DropdownItemProps,
  DropdownGroupProps,
  DropdownSeparatorProps,
} from "./Dropdown.types";
import { ChevronDown, Check, Search, X } from "lucide-react";
import {
  createValidator,
  commonValidators,
  isValidBoolean,
  isValidNumber,
} from "../../lib/validation";
import {
  dropdownTriggerVariants,
  dropdownContentVariants,
  dropdownItemVariants,
} from "./Dropdown.styles";

/**
 * Dropdown Component
 *
 * Advanced dropdown menu with nested menus, search, keyboard navigation,
 * command palette, and beautiful animations.
 *
 * Supports TWO usage patterns:
 * 1. Props-based (Simple): <Dropdown options={[...]} placeholder="Select..." />
 * 2. Component-based (Flexible): <Dropdown><DropdownTrigger>...</DropdownTrigger><DropdownContent>...</DropdownContent></Dropdown>
 *
 * @variant default | primary | secondary | accent | success | warning | error | ghost | glass
 * @size sm | md | lg
 */

// Dropdown Context
interface DropdownContextValue {
  value?: string | string[];
  onChange?: (value: string) => void;
  multiple?: boolean;
  closeOnSelect?: boolean;
  checkmarks?: boolean;
  onClose?: () => void;
  searchQuery?: string;
  isOpen?: boolean;
  setIsOpen?: (open: boolean) => void;
  variant?: DropdownProps["variant"];
  size?: DropdownProps["size"];
  disabled?: boolean;
}

const DropdownContext = createContext<DropdownContextValue | undefined>(
  undefined
);

const useDropdownContext = () => {
  const context = useContext(DropdownContext);
  if (!context) {
    throw new Error("Dropdown components must be used within Dropdown");
  }
  return context;
};



/**
 * Dropdown Component (Compound Pattern)
 */
export const Dropdown = forwardRef<HTMLDivElement, DropdownProps>(
  (
    {
      trigger,
      triggerAsChild,
      options,
      children,
      value: controlledValue,
      defaultValue,
      onChange,
      open: controlledOpen,
      defaultOpen = false,
      onOpenChange,
      multiple = false,
      closeOnSelect = !multiple,
      modal = false,
      searchable = false,
      searchPlaceholder = "Search...",
      onSearch,
      variant = "default",
      size = "md",
      align = "start",
      side = "bottom",
      sideOffset = 4,
      width,
      maxHeight = "320px",
      label,
      placeholder = "Select...",
      error,
      helperText,
      commandPalette = false,
      grouped = false,
      checkmarks = true,
      disabled = false,
      loading = false,
      emptyMessage = "No results found",
      className,
      triggerClassName,
      contentClassName,
      ...props
    },
    ref
  ) => {
    // Development-only validation
    useEffect(() => {
      const validator = createValidator("Dropdown");

      // Validate variant
      validator.validateEnum("variant", variant, [
        "default",
        "primary",
        "secondary",
        "accent",
        "success",
        "warning",
        "error",
        "ghost",
        "glass",
      ] as const);

      // Validate size
      validator.validateEnum("size", size, ["sm", "md", "lg"] as const);

      // Validate align
      validator.validateEnum("align", align, [
        "start",
        "center",
        "end",
      ] as const);

      // Validate side
      validator.validateEnum("side", side, [
        "top",
        "right",
        "bottom",
        "left",
      ] as const);

      // Validate numeric props
      validator.validateType("sideOffset", sideOffset, "number", isValidNumber);

      // Validate boolean props
      validator.validateType("multiple", multiple, "boolean", isValidBoolean);
      validator.validateType(
        "closeOnSelect",
        closeOnSelect,
        "boolean",
        isValidBoolean
      );
      validator.validateType("modal", modal, "boolean", isValidBoolean);
      validator.validateType(
        "searchable",
        searchable,
        "boolean",
        isValidBoolean
      );
      validator.validateType(
        "commandPalette",
        commandPalette,
        "boolean",
        isValidBoolean
      );
      validator.validateType("grouped", grouped, "boolean", isValidBoolean);
      validator.validateType(
        "checkmarks",
        checkmarks,
        "boolean",
        isValidBoolean
      );
      validator.validateType("disabled", disabled, "boolean", isValidBoolean);
      validator.validateType("loading", loading, "boolean", isValidBoolean);

      if (triggerAsChild !== undefined) {
        validator.validateType(
          "triggerAsChild",
          triggerAsChild,
          "boolean",
          isValidBoolean
        );
      }

      // Common validators
      commonValidators.className(validator, className);
    }, [
      variant,
      size,
      align,
      side,
      sideOffset,
      multiple,
      closeOnSelect,
      modal,
      searchable,
      commandPalette,
      grouped,
      checkmarks,
      disabled,
      loading,
      triggerAsChild,
      className,
    ]);

    // Detect usage mode: Component-based vs Props-based
    const isComponentBased = !options && children;

    const [uncontrolledOpen, setUncontrolledOpen] = useState(defaultOpen);
    const [uncontrolledValue, setUncontrolledValue] = useState<
      string | string[]
    >(defaultValue || (multiple ? [] : ""));
    const [searchQuery, setSearchQuery] = useState("");
    const [focusedIndex, setFocusedIndex] = useState(0);

    const isControlledOpen = controlledOpen !== undefined;
    const isControlledValue = controlledValue !== undefined;
    const isOpen = isControlledOpen ? controlledOpen : uncontrolledOpen;
    const value = isControlledValue ? controlledValue : uncontrolledValue;

    const contentRef = useRef<HTMLDivElement>(null);
    const searchInputRef = useRef<HTMLInputElement>(null);

    const handleOpenChange = (newOpen: boolean) => {
      if (!isControlledOpen) {
        setUncontrolledOpen(newOpen);
      }
      onOpenChange?.(newOpen);
      if (!newOpen) {
        setSearchQuery("");
        setFocusedIndex(0);
      }
    };

    const handleValueChange = (newValue: string) => {
      if (disabled) return;

      let updatedValue: string | string[];
      if (multiple) {
        const currentArray = Array.isArray(value) ? value : [];
        if (currentArray.includes(newValue)) {
          updatedValue = currentArray.filter((v) => v !== newValue);
        } else {
          updatedValue = [...currentArray, newValue];
        }
      } else {
        updatedValue = newValue;
      }

      if (!isControlledValue) {
        setUncontrolledValue(updatedValue);
      }
      onChange?.(updatedValue);

      if (closeOnSelect && !multiple) {
        handleOpenChange(false);
      }
    };

    const handleClose = () => {
      handleOpenChange(false);
    };

    // Keyboard navigation
    useEffect(() => {
      if (!isOpen) return;

      const handleKeyDown = (e: KeyboardEvent) => {
        if (e.key === "Escape") {
          handleClose();
        } else if (e.key === "ArrowDown") {
          e.preventDefault();
          setFocusedIndex((prev) =>
            Math.min(prev + 1, (options?.length || 1) - 1)
          );
        } else if (e.key === "ArrowUp") {
          e.preventDefault();
          setFocusedIndex((prev) => Math.max(prev - 1, 0));
        } else if (e.key === "Enter") {
          e.preventDefault();
          const option = options?.[focusedIndex];
          if (option && !option.disabled) {
            handleValueChange(option.value);
          }
        }
      };

      document.addEventListener("keydown", handleKeyDown);
      return () => document.removeEventListener("keydown", handleKeyDown);
    }, [isOpen, focusedIndex, options]);

    // Focus search input when opened
    useEffect(() => {
      if (isOpen && searchable && searchInputRef.current) {
        setTimeout(() => searchInputRef.current?.focus(), 100);
      }
    }, [isOpen, searchable]);

    // Filter options by search
    const filteredOptions = options?.filter((option) => {
      if (!searchQuery) return true;
      return (
        option.label.toLowerCase().includes(searchQuery.toLowerCase()) ||
        option.description?.toLowerCase().includes(searchQuery.toLowerCase())
      );
    });

    // Get display value
    const getDisplayValue = () => {
      if (!value) return placeholder;
      if (multiple && Array.isArray(value)) {
        if (value.length === 0) return placeholder;
        const selected = options?.filter((opt) => value.includes(opt.value));
        return selected?.map((opt) => opt.label).join(", ") || placeholder;
      }
      const selected = options?.find((opt) => opt.value === value);
      return selected?.label || placeholder;
    };

    const contextValue: DropdownContextValue = {
      value,
      onChange: handleValueChange,
      multiple,
      closeOnSelect,
      checkmarks,
      onClose: handleClose,
      searchQuery,
      isOpen,
      setIsOpen: handleOpenChange,
      variant,
      size,
      disabled,
    };

    // Click outside to close - optimized with useClickOutside hook
    useClickOutside(contentRef, () => {
      if (isOpen && !modal) {
        handleClose();
      }
    });

    // Component-based mode: render children with context
    if (isComponentBased) {
      return (
        <DropdownContext.Provider value={contextValue}>
          <div
            ref={ref}
            className={cn("relative inline-block", className)}
            {...props}
          >
            {children}
            {/* Error/Helper Text */}
            {error && (
              <p className="mt-2 text-sm font-medium text-destructive">
                {error}
              </p>
            )}
            {!error && helperText && (
              <p className="mt-2 text-sm text-muted-foreground">{helperText}</p>
            )}
          </div>
        </DropdownContext.Provider>
      );
    }

    // Props-based mode: render with options prop
    return (
      <div
        ref={ref}
        className={cn("relative inline-block", className)}
        {...props}
      >
        {label && (
          <label className="mb-2 block text-sm font-semibold text-foreground">
            {label}
          </label>
        )}

        {/* Trigger */}
        <button
          type="button"
          onClick={() => !disabled && handleOpenChange(!isOpen)}
          disabled={disabled || loading}
          className={cn(
            dropdownTriggerVariants({ variant, size }),
            width && `w-[${width}]`,
            triggerClassName
          )}
          aria-haspopup="listbox"
          aria-expanded={isOpen}
        >
          <span className="flex-1 truncate text-left">
            {loading ? "Loading..." : getDisplayValue()}
          </span>
          <ChevronDown
            className={cn(
              "h-4 w-4 shrink-0 transition-transform duration-300",
              isOpen && "rotate-180"
            )}
          />
        </button>

        {/* Content */}
        {isOpen && (
          <DropdownContext.Provider value={contextValue}>
            <div
              ref={contentRef}
              className={cn(
                dropdownContentVariants({ variant }),
                "absolute mt-2 w-full min-w-[200px]",
                side === "top" && "bottom-full mb-2 mt-0",
                side === "bottom" && "top-full",
                align === "start" && "left-0",
                align === "center" && "left-1/2 -translate-x-1/2",
                align === "end" && "right-0",
                width && `w-[${width}]`,
                contentClassName
              )}
              style={{
                maxHeight,
              }}
            >
              {/* Search */}
              {searchable && (
                <div className="border-b-2 border-border p-2">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
                    <input
                      ref={searchInputRef}
                      type="text"
                      placeholder={searchPlaceholder}
                      value={searchQuery}
                      onChange={(e) => {
                        setSearchQuery(e.target.value);
                        onSearch?.(e.target.value);
                      }}
                      className={cn(
                        "w-full rounded-lg border-2 border-border bg-background",
                        "py-2 pl-10 pr-8 text-sm",
                        "focus:border-primary focus:outline-none focus:ring-2 focus:ring-primary/50",
                        "transition-all duration-200"
                      )}
                    />
                    {searchQuery && (
                      <button
                        onClick={() => setSearchQuery("")}
                        className="absolute right-2 top-1/2 -translate-y-1/2 rounded-md p-1 hover:bg-muted"
                      >
                        <X className="h-3 w-3" />
                      </button>
                    )}
                  </div>
                </div>
              )}

              {/* Options */}
              <div className="max-h-[280px] overflow-y-auto p-1">
                {filteredOptions && filteredOptions.length > 0 ? (
                  filteredOptions.map((option, index) => (
                    <DropdownItem
                      key={option.value}
                      value={option.value}
                      label={option.label}
                      icon={option.icon}
                      avatar={option.avatar}
                      description={option.description}
                      badge={option.badge}
                      shortcut={option.shortcut}
                      color={option.color}
                      disabled={option.disabled}
                      divider={option.divider}
                      header={option.header}
                      data-focused={index === focusedIndex}
                      className={cn(index === focusedIndex && "bg-muted/50")}
                    />
                  ))
                ) : (
                  <div className="py-8 text-center text-sm text-muted-foreground">
                    {emptyMessage}
                  </div>
                )}
              </div>
            </div>
          </DropdownContext.Provider>
        )}

        {/* Error/Helper Text */}
        {error && (
          <p className="mt-2 text-sm font-medium text-destructive">{error}</p>
        )}
        {!error && helperText && (
          <p className="mt-2 text-sm text-muted-foreground">{helperText}</p>
        )}
      </div>
    );
  }
);

Dropdown.displayName = "Dropdown";

/**
 * DropdownItem Component
 */
export const DropdownItem = forwardRef<HTMLDivElement, DropdownItemProps>(
  (
    {
      value = "",
      icon,
      avatar,
      label,
      description,
      badge,
      shortcut,
      color,
      disabled = false,
      divider = false,
      header = false,
      children,
      onSelect,
      className,
      ...props
    },
    ref
  ) => {
    const context = useDropdownContext();
    const isSelected = context?.multiple
      ? Array.isArray(context.value) && context.value.includes(value)
      : context?.value === value;

    const handleClick = () => {
      if (disabled) return;
      context?.onChange?.(value);
      onSelect?.(value);
    };

    if (divider) {
      return <DropdownSeparator />;
    }

    if (header) {
      return (
        <div
          ref={ref}
          className={cn(
            "px-4 py-2 text-xs font-semibold uppercase tracking-wider text-muted-foreground",
            className
          )}
          {...props}
        >
          {label || children}
        </div>
      );
    }

    return (
      <div
        ref={ref}
        onClick={handleClick}
        data-disabled={disabled ? "true" : undefined}
        data-selected={isSelected ? "true" : "false"}
        className={cn(dropdownItemVariants({ variant: "default" }), className)}
        {...props}
      >
        {/* Checkmark */}
        {context?.checkmarks && context.multiple && (
          <div className="flex h-4 w-4 shrink-0 items-center justify-center">
            {isSelected && <Check className="h-4 w-4" />}
          </div>
        )}

        {/* Avatar */}
        {avatar && (
          <img
            src={avatar}
            alt={label}
            className="h-6 w-6 shrink-0 rounded-full object-cover"
          />
        )}

        {/* Icon */}
        {icon && <span className="shrink-0">{icon}</span>}

        {/* Content */}
        <div className="flex flex-1 flex-col">
          <div className="flex items-center gap-2">
            {label && (
              <span
                className={cn(
                  "font-semibold leading-none text-foreground",
                  color && ""
                )}
                style={color ? { color } : undefined}
              >
                {label}
              </span>
            )}
            {badge && (
              <span className="rounded bg-primary/20 px-1.5 py-0.5 text-xs font-semibold text-primary">
                {badge}
              </span>
            )}
          </div>
          {description && (
            <span className="mt-1 text-xs leading-tight text-muted-foreground">
              {description}
            </span>
          )}
        </div>

        {/* Shortcut */}
        {shortcut && (
          <span className="ml-auto shrink-0 rounded bg-muted px-1.5 py-0.5 text-xs font-medium text-muted-foreground">
            {shortcut}
          </span>
        )}

        {/* Single select checkmark */}
        {context?.checkmarks && !context.multiple && isSelected && (
          <Check className="ml-auto h-4 w-4 shrink-0" />
        )}

        {children}
      </div>
    );
  }
);

DropdownItem.displayName = "DropdownItem";

/**
 * DropdownSeparator Component
 */
export const DropdownSeparator = forwardRef<
  HTMLDivElement,
  DropdownSeparatorProps
>(({ className, ...props }, ref) => {
  return (
    <div
      ref={ref}
      className={cn("my-1 h-px bg-border", className)}
      {...props}
    />
  );
});

DropdownSeparator.displayName = "DropdownSeparator";

/**
 * DropdownGroup Component
 */
export const DropdownGroup = forwardRef<HTMLDivElement, DropdownGroupProps>(
  ({ label, children, className, ...props }, ref) => {
    return (
      <div ref={ref} className={cn("py-1", className)} {...props}>
        {label && (
          <div className="px-4 py-2 text-xs font-semibold uppercase tracking-wider text-muted-foreground">
            {label}
          </div>
        )}
        {children}
      </div>
    );
  }
);

DropdownGroup.displayName = "DropdownGroup";

/**
 * DropdownTrigger Component
 * For component-based usage
 */
export const DropdownTrigger = forwardRef<
  HTMLButtonElement,
  DropdownTriggerProps
>(({ children, className, asChild = false, disabled, ...props }, ref) => {
  const context = useDropdownContext();
  const {
    isOpen,
    setIsOpen,
    variant,
    size,
    disabled: contextDisabled,
  } = context;

  const isDisabled = disabled || contextDisabled;

  if (asChild && isValidElement(children)) {
    return cloneElement(children as React.ReactElement<any>, {
      onClick: () => !isDisabled && setIsOpen?.(!isOpen),
      "aria-haspopup": "listbox",
      "aria-expanded": isOpen,
      disabled: isDisabled,
    });
  }

  return (
    <button
      ref={ref}
      type="button"
      onClick={() => !isDisabled && setIsOpen?.(!isOpen)}
      disabled={isDisabled}
      className={cn(dropdownTriggerVariants({ variant, size }), className)}
      aria-haspopup="listbox"
      aria-expanded={isOpen}
      {...props}
    >
      <span className="flex-1 truncate text-left">{children}</span>
      <ChevronDown
        className={cn(
          "h-4 w-4 shrink-0 transition-transform duration-300",
          isOpen && "rotate-180"
        )}
      />
    </button>
  );
});

DropdownTrigger.displayName = "DropdownTrigger";

/**
 * DropdownContent Component
 * For component-based usage
 */
export const DropdownContent = forwardRef<HTMLDivElement, DropdownContentProps>(
  (
    {
      children,
      className,
      align = "start",
      side = "bottom",
      sideOffset = 4,
      width,
      maxHeight = "320px",
      ...props
    },
    ref
  ) => {
    const context = useDropdownContext();
    const { isOpen, variant } = context;

    if (!isOpen) return null;

    return (
      <div
        ref={ref}
        className={cn(
          dropdownContentVariants({ variant }),
          "absolute mt-2 w-full min-w-[200px]",
          side === "top" && "bottom-full mb-2 mt-0",
          side === "bottom" && "top-full",
          side === "left" && "right-full mr-2 mt-0",
          side === "right" && "left-full ml-2 mt-0",
          align === "start" && "left-0",
          align === "center" && "left-1/2 -translate-x-1/2",
          align === "end" && "right-0",
          width && `w-[${width}]`,
          className
        )}
        style={{
          maxHeight,
        }}
        {...props}
      >
        <div className="max-h-[280px] overflow-y-auto p-1">{children}</div>
      </div>
    );
  }
);

DropdownContent.displayName = "DropdownContent";

export default Dropdown;

"use client";

import React, { createContext, useContext, useCallback } from "react";
import { cn } from "../../lib/utils";
import type {
  FormProps,
  FormFieldProps,
  FormItemProps,
  FormLabelProps,
  FormControlProps,
  FormDescriptionProps,
  FormMessageProps,
  FormCompactProps,
  FormSectionProps,
  FormActionsProps,
  FormVariant,
  FormSize,
  FormLayout,
  FormFieldConfig,
} from "./Form.types";
import {
  formItemVariants,
  formLabelVariants,
  formControlVariants,
  formDescriptionVariants,
  formSectionVariants,
  formSectionTitleVariants,
  formActionsVariants,
} from "./Form.styles";
import Button from "../Button";
import { Input } from "../Input";
import { TextArea } from "../TextArea";

// ===========================
// Optional React Hook Form Support
// ===========================

/**
 * The Form component supports both standalone and React Hook Form integration.
 *
 * STANDALONE MODE (default):
 * - Works out of the box without any additional dependencies
 * - Uses native form submission with FormData
 * - Example:
 *   <form onSubmit={handleSubmit}>
 *     <FormItem>
 *       <FormLabel>Email</FormLabel>
 *       <FormControl>
 *         <Input name="email" />
 *       </FormControl>
 *     </FormItem>
 *   </form>
 *
 * REACT HOOK FORM MODE (shadcn/ui pattern):
 * - Install: npm install react-hook-form @hookform/resolvers zod
 * - Example:
 *   const form = useForm({ resolver: zodResolver(schema) });
 *
 *   <Form {...form}>
 *     <form onSubmit={form.handleSubmit(onSubmit)}>
 *       <FormField
 *         control={form.control}
 *         name="username"
 *         render={({ field }) => (
 *           <FormItem>
 *             <FormLabel>Username</FormLabel>
 *             <FormControl>
 *               <Input {...field} />
 *             </FormControl>
 *             <FormMessage />
 *           </FormItem>
 *         )}
 *       />
 *     </form>
 *   </Form>
 */

// Try to import from react-hook-form if available
const Controller: any = null;
const FormProvider: any = null;
const useFormContext: any = () => ({
  getFieldState: () => ({}),
  formState: {},
});

// We'll check if react-hook-form is available at runtime
// This allows the component to work standalone or with RHF

// ===========================
// Form Context
// ===========================

type FormFieldContextValue<TName extends string = string> = {
  name: TName;
};

const FormFieldContext = createContext<FormFieldContextValue>(
  {} as FormFieldContextValue,
);

type FormItemContextValue = {
  id: string;
};

const FormItemContext = createContext<FormItemContextValue>(
  {} as FormItemContextValue,
);

const useFormField = () => {
  const fieldContext = useContext(FormFieldContext);
  const itemContext = useContext(FormItemContext);
  const { getFieldState, formState } = useFormContext();

  const fieldState = getFieldState(fieldContext.name, formState);

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>");
  }

  const { id } = itemContext;

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  };
};

interface FormContextValue {
  variant?: FormVariant;
  size?: FormSize;
  layout?: FormLayout;
  disabled?: boolean;
}

const FormContext = createContext<FormContextValue>({});

const useFormConfig = () => useContext(FormContext);

/**
 * Form - Works in both standalone mode and with React Hook Form
 *
 * STANDALONE MODE (for backward compatibility):
 * ```tsx
 * <Form onSubmit={handleSubmit} variant="default">
 *   <FormItem>
 *     <FormLabel>Username</FormLabel>
 *     <FormControl>
 *       <Input name="username" />
 *     </FormControl>
 *   </FormItem>
 * </Form>
 * ```
 *
 * REACT HOOK FORM MODE (shadcn/ui pattern):
 * ```tsx
 * const form = useForm({ resolver: zodResolver(schema) });
 *
 * <Form {...form}>
 *   <form onSubmit={form.handleSubmit(onSubmit)}>
 *     <FormField
 *       control={form.control}
 *       name="username"
 *       render={({ field }) => (
 *         <FormItem>
 *           <FormLabel>Username</FormLabel>
 *           <FormControl>
 *             <Input {...field} />
 *           </FormControl>
 *           <FormMessage />
 *         </FormItem>
 *       )}
 *     />
 *   </form>
 * </Form>
 * ```
 */
export const Form = React.forwardRef<HTMLFormElement, FormProps>(
  (
    {
      children,
      onSubmit,
      variant = "default",
      size = "md",
      layout = "vertical",
      loading = false,
      disabled = false,
      className,
      ...props
    },
    ref,
  ) => {
    // If FormProvider is available and props look like RHF (has control, etc.),
    // use FormProvider. Otherwise, render as a regular form.
    const isReactHookForm = FormProvider && (props as any).control;

    const formConfig: FormContextValue = {
      variant,
      size,
      layout,
      disabled: disabled || loading,
    };

    if (isReactHookForm && FormProvider) {
      // React Hook Form mode - just return FormProvider
      return (
        <FormContext.Provider value={formConfig}>
          <FormProvider {...props}>{children}</FormProvider>
        </FormContext.Provider>
      );
    }

    // Standalone mode - render as regular form element
    return (
      <FormContext.Provider value={formConfig}>
        <form
          ref={ref}
          onSubmit={onSubmit}
          className={cn("space-y-6", className)}
          {...props}
        >
          {children}
        </form>
      </FormContext.Provider>
    );
  },
);

Form.displayName = "Form";

/**
 * FormField - Field component with render prop pattern
 *
 * Usage:
 * ```tsx
 * <FormField
 *   control={form.control}
 *   name="username"
 *   render={({ field }) => (
 *     <FormItem>
 *       <FormLabel>Username</FormLabel>
 *       <FormControl>
 *         <Input {...field} />
 *       </FormControl>
 *       <FormMessage />
 *     </FormItem>
 *   )}
 * />
 * ```
 */
export const FormField = ({ ...props }: any) => {
  // If Controller is available (react-hook-form installed)
  if (Controller) {
    return (
      <FormFieldContext.Provider value={{ name: props.name }}>
        <Controller {...props} />
      </FormFieldContext.Provider>
    );
  }

  // Standalone mode - just render children with empty field
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      {props.render?.({
        field: {
          name: props.name,
          value: "",
          onChange: () => {},
          onBlur: () => {},
          ref: null,
        },
      })}
    </FormFieldContext.Provider>
  );
};

FormField.displayName = "FormField";

// ===========================
// FormItem Component
// ===========================

/**
 * FormItem - Container for form field elements
 */
export const FormItem = React.forwardRef<HTMLDivElement, FormItemProps>(
  ({ variant, layout, className, children, ...props }, ref) => {
    const config = useFormConfig();

    return (
      <div
        ref={ref}
        className={cn(
          formItemVariants({
            variant: variant || config.variant,
            layout: layout || config.layout,
          }),
          className,
        )}
        {...props}
      >
        {children}
      </div>
    );
  },
);

FormItem.displayName = "FormItem";

// ===========================
// FormLabel Component
// ===========================

/**
 * FormLabel - Label for form fields
 */
export const FormLabel = React.forwardRef<HTMLLabelElement, FormLabelProps>(
  (
    {
      required = false,
      optional = false,
      variant,
      size,
      className,
      children,
      ...props
    },
    ref,
  ) => {
    const config = useFormConfig();

    return (
      <label
        ref={ref}
        className={cn(
          formLabelVariants({
            variant: variant || config.variant,
            size: size || config.size,
            required,
          }),
          className,
        )}
        {...props}
      >
        {children}
        {required && (
          <span className="text-error ml-1" aria-label="required">
            *
          </span>
        )}
        {optional && (
          <span className="text-muted-foreground ml-1 text-xs font-normal">
            (optional)
          </span>
        )}
      </label>
    );
  },
);

FormLabel.displayName = "FormLabel";

// ===========================
// FormControl Component
// ===========================

/**
 * FormControl - Wrapper for form control elements
 */
export const FormControl = React.forwardRef<HTMLDivElement, FormControlProps>(
  ({ className, children, ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(formControlVariants(), className)}
        {...props}
      >
        {children}
      </div>
    );
  },
);

FormControl.displayName = "FormControl";

// ===========================
// FormDescription Component
// ===========================

/**
 * FormDescription - Helper text for form fields
 */
export const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  FormDescriptionProps
>(({ variant, size, className, children, ...props }, ref) => {
  const config = useFormConfig();

  return (
    <p
      ref={ref}
      className={cn(
        formDescriptionVariants({
          variant: variant || config.variant,
          size: size || config.size,
        }),
        className,
      )}
      {...props}
    >
      {children}
    </p>
  );
});

FormDescription.displayName = "FormDescription";

// ===========================
// FormMessage Component
// ===========================

/**
 * FormMessage - Error/info messages for form fields
 * Supports both auto-detection from React Hook Form and manual messages
 */
export const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  FormMessageProps
>(({ className, children, variant, ...props }, ref) => {
  const itemContext = useContext(FormItemContext);

  // Try to get error from React Hook Form if context is available
  let error;
  let formMessageId;
  try {
    const { error: fieldError, formMessageId: msgId } = useFormField();
    error = fieldError;
    formMessageId = msgId;
  } catch {
    // Not in a FormField context, use manually
    formMessageId = itemContext?.id
      ? `${itemContext.id}-form-item-message`
      : undefined;
  }

  const body = error ? String(error?.message) : children;

  if (!body) {
    return null;
  }

  // Determine the variant class based on variant prop or error
  const variantClass =
    variant === "success"
      ? "text-success"
      : variant === "warning"
        ? "text-warning"
        : variant === "info"
          ? "text-info"
          : "text-destructive"; // error is default

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium", variantClass, className)}
      {...props}
    >
      {body}
    </p>
  );
});

FormMessage.displayName = "FormMessage";

// ===========================
// FormSection Component
// ===========================

/**
 * FormSection - Group related form fields
 */
export const FormSection = React.forwardRef<HTMLDivElement, FormSectionProps>(
  (
    {
      title,
      description,
      variant = "default",
      divider = false,
      className,
      children,
      ...props
    },
    ref,
  ) => {
    return (
      <div
        ref={ref}
        className={cn(formSectionVariants({ variant, divider }), className)}
        {...props}
      >
        {(title || description) && (
          <div className="mb-4">
            {title && (
              <h3 className={formSectionTitleVariants({ variant })}>{title}</h3>
            )}
            {description && (
              <p className="text-sm text-muted-foreground">{description}</p>
            )}
          </div>
        )}
        {children}
      </div>
    );
  },
);

FormSection.displayName = "FormSection";

// ===========================
// FormActions Component
// ===========================

/**
 * FormActions - Container for form action buttons
 */
export const FormActions = React.forwardRef<HTMLDivElement, FormActionsProps>(
  ({ align = "right", variant, className, children, ...props }, ref) => {
    const config = useFormConfig();

    return (
      <div
        ref={ref}
        className={cn(
          formActionsVariants({
            align,
            variant: variant || config.variant,
