    menuId?: string;
    isOpen?: boolean;
    onOpenChange?: (open: boolean) => void;
  }
>(
  (
    {
      children,
      disabled = false,
      className,
      menuId,
      isOpen,
      onOpenChange,
      ...props
    },
    ref
  ) => {
    const context = useMenubarContext();
    const handleClick = () => {
      if (!disabled && onOpenChange) {
        onOpenChange(!isOpen);
      }
    };

    return (
      <button
        ref={ref}
        type="button"
        disabled={disabled}
        className={cn(
          menubarTriggerVariants({ disabled, variant: context.variant }),
          className
        )}
        data-state={isOpen ? "open" : "closed"}
        aria-haspopup="menu"
