// ===========================
// MenubarContent Component
// ===========================

/**
 * MenubarContent - Container for menu items
 */
export const MenubarContent = React.forwardRef<
  HTMLDivElement,
  MenubarContentProps & {
    menuId?: string;
    isOpen?: boolean;
    onOpenChange?: (open: boolean) => void;
  }
>(
  (
    {
      children,
      align = "start",
      side = "bottom",
      className,
      menuId,
      isOpen,
      onOpenChange,
      ...props
    },
    ref
  ) => {
    const contentRef = React.useRef<HTMLDivElement>(null);

    // Handle click outside to close
    React.useEffect(() => {
      if (!isOpen) return;

      const handleClickOutside = (event: MouseEvent) => {
        if (
          contentRef.current &&
          !contentRef.current.contains(event.target as Node)
        ) {
          onOpenChange?.(false);
        }
      };

      document.addEventListener("mousedown", handleClickOutside);
      return () =>
        document.removeEventListener("mousedown", handleClickOutside);
    }, [isOpen, onOpenChange]);

    // Runtime validation
    const validator = new ComponentValidator("MenubarContent");

    React.useEffect(() => {
      if (align && !VALID_ALIGNS.includes(align)) {
        validator.error(
          `Invalid prop 'align': must be one of [${VALID_ALIGNS.join(
            ", "
          )}], got '${align}'.`
        );
      }

      if (side && !VALID_SIDES.includes(side)) {
        validator.error(
          `Invalid prop 'side': must be one of [${VALID_SIDES.join(
            ", "
          )}], got '${side}'.`
        );
      }
    }, [align, side]);

    if (!isOpen) return null;

    const context = useMenubarContext();

    return (
      <div
        ref={(node) => {
          contentRef.current = node;
          if (typeof ref === "function") ref(node);
          else if (ref) ref.current = node;
        }}
        className={cn(
          menubarContentVariants({ align, side, variant: context.variant }),
          className
        )}
        role="menu"
        data-state={isOpen ? "open" : "closed"}
        data-side={side}
        {...props}
      >
        {children}
      </div>
    );
  }
);

