const RadioContext = createContext<RadioContextValue | undefined>(undefined);

const useRadioContext = () => {
  return useContext(RadioContext);
};



/**
 * Radio component
 */
export const Radio = forwardRef<HTMLInputElement, RadioCardProps>(
  (
    {
      label,
      description,
      error,
      helperText,
      labelPosition = "right",
      variant = "primary",
      size = "md",
      className,
      disabled,
      card = false,
      icon,
      badge,
      image,
