"use client";

import React, { useState, useRef, useEffect } from "react";
import { cn } from "../../lib/utils";
import type { RatingProps } from "./Rating.types";
import { Star, Heart, Circle, Diamond } from "lucide-react";
import {
  createValidator,
  commonValidators,
  isValidBoolean,
  isValidNumber,
} from "../../lib/validation";
import { ratingVariants, iconVariants } from "./Rating.styles";

/**
 * Get icon component based on icon type
 */
const getIconComponent = (icon: RatingProps["icon"]) => {
  switch (icon) {
    case "heart":
      return Heart;
    case "circle":
      return Circle;
    case "diamond":
      return Diamond;
    case "star":
    default:
      return Star;
  }
};

/**
 * Rating Component
 *
 * A flexible rating component with support for different icons, variants, and interactions.
 *
 * @example
 * ```tsx
 * <Rating value={4} max={5} />
 * <Rating value={3.5} precision="half" showValue />
 * <Rating value={4} variant="gradient" size="lg" icon="heart" />
 * ```
 */
const Rating = React.forwardRef<HTMLDivElement, RatingProps>(
  (
    {
      value = 0,
      max = 5,
      variant = "default",
      size = "md",
      icon = "star",
      precision = "full",
      readOnly = false,
      disabled = false,
      showValue = false,
      valueFormat,
      count,
      countLabel = "reviews",
      color,
      emptyColor,
      hoverable = !readOnly,
      showTooltip = false,
      tooltipLabels,
      onChange,
      onHover,
      allowClear = true,
      customIcon,
      customEmptyIcon,
      className,
      iconClassName,
      animated = true,
      ...props
    },
    ref
  ) => {
    // Development-only validation
    useEffect(() => {
      const validator = createValidator("Rating");

      // Validate variant
      validator.validateEnum("variant", variant, [
        "default",
        "primary",
        "secondary",
        "gradient",
        "glass",
        "outline",
      ] as const);

      // Validate size
      validator.validateEnum("size", size, ["sm", "md", "lg", "xl"] as const);

      // Validate icon
      validator.validateEnum("icon", icon, [
        "star",
        "heart",
        "circle",
        "diamond",
      ] as const);

      // Validate precision
      validator.validateEnum("precision", precision, ["full", "half"] as const);

      // Validate numeric props
      validator.validateType("value", value, "number", isValidNumber);
      validator.validateType("max", max, "number", isValidNumber);

      if (max <= 0) {
        validator.error("max must be greater than 0");
      }

      if (value < 0) {
        validator.warn("value should not be negative");
      }

      if (value > max) {
        validator.warn("value should not exceed max");
      }

      if (count !== undefined) {
        validator.validateType("count", count, "number", isValidNumber);
        if (count < 0) {
          validator.warn("count should not be negative");
        }
      }

      // Validate boolean props
      validator.validateType("readOnly", readOnly, "boolean", isValidBoolean);
      validator.validateType("disabled", disabled, "boolean", isValidBoolean);
      validator.validateType("showValue", showValue, "boolean", isValidBoolean);
      validator.validateType("hoverable", hoverable, "boolean", isValidBoolean);
      validator.validateType(
        "showTooltip",
        showTooltip,
        "boolean",
        isValidBoolean
      );
      validator.validateType(
        "allowClear",
        allowClear,
        "boolean",
        isValidBoolean
      );
      validator.validateType("animated", animated, "boolean", isValidBoolean);

      // Common validators
      commonValidators.className(validator, className);
    }, [
      variant,
      size,
      icon,
      precision,
      value,
      max,
      count,
      readOnly,
      disabled,
      showValue,
      hoverable,
      showTooltip,
      allowClear,
      animated,
      className,
    ]);

    const [hoverValue, setHoverValue] = useState<number | null>(null);
    const [focusedIndex, setFocusedIndex] = useState<number | null>(null);
    const containerRef = useRef<HTMLDivElement>(null);

    const IconComponent = getIconComponent(icon);
    const displayValue = hoverValue !== null ? hoverValue : value;
    const isInteractive = !readOnly && !disabled;

    /**
     * Calculate the fill state of an icon
     */
    const getIconState = (index: number): "empty" | "filled" | "half" => {
      const iconValue = index + 1;

      if (precision === "half") {
        if (displayValue >= iconValue) return "filled";
        if (displayValue >= iconValue - 0.5) return "half";
        return "empty";
      }

      return displayValue >= iconValue ? "filled" : "empty";
    };

    /**
     * Calculate rating value based on click position
     */
    const calculateRating = (
      index: number,
      event: React.MouseEvent
    ): number => {
      if (precision === "full") {
        return index + 1;
      }

      // For half precision, check if click was on left or right half
      const target = event.currentTarget as HTMLElement;
      const rect = target.getBoundingClientRect();
      const clickX = event.clientX - rect.left;
      const halfWidth = rect.width / 2;

      return clickX < halfWidth ? index + 0.5 : index + 1;
    };

    /**
     * Handle icon click
     */
    const handleClick = (index: number, event: React.MouseEvent) => {
      if (!isInteractive) return;

      const newValue = calculateRating(index, event);

      // Allow clearing by clicking the same value
      if (allowClear && newValue === value) {
        onChange?.(0);
      } else {
        onChange?.(newValue);
      }
    };

    /**
     * Handle mouse enter on icon
     */
    const handleMouseEnter = (index: number, event: React.MouseEvent) => {
      if (!isInteractive || !hoverable) return;

      const newValue = calculateRating(index, event);
      setHoverValue(newValue);
      onHover?.(newValue);
    };

    /**
     * Handle mouse move on icon (for half precision)
     */
    const handleMouseMove = (index: number, event: React.MouseEvent) => {
      if (!isInteractive || !hoverable || precision === "full") return;

      const newValue = calculateRating(index, event);
      if (newValue !== hoverValue) {
        setHoverValue(newValue);
        onHover?.(newValue);
      }
    };

    /**
     * Handle mouse leave
     */
    const handleMouseLeave = () => {
      if (!isInteractive) return;
      setHoverValue(null);
    };

    /**
     * Handle keyboard navigation
     */
    const handleKeyDown = (event: React.KeyboardEvent) => {
      if (!isInteractive) return;

      let newValue = value;

      switch (event.key) {
        case "ArrowRight":
        case "ArrowUp":
          event.preventDefault();
          newValue = Math.min(max, value + (precision === "half" ? 0.5 : 1));
          break;
        case "ArrowLeft":
        case "ArrowDown":
          event.preventDefault();
          newValue = Math.max(0, value - (precision === "half" ? 0.5 : 1));
          break;
        case "Home":
          event.preventDefault();
          newValue = precision === "half" ? 0.5 : 1;
          break;
        case "End":
          event.preventDefault();
          newValue = max;
          break;
        case "0":
        case "Backspace":
        case "Delete":
          if (allowClear) {
            event.preventDefault();
            newValue = 0;
          }
          break;
        default:
          // Handle number keys 1-9
          const num = parseInt(event.key);
          if (!isNaN(num) && num >= 0 && num <= max) {
            event.preventDefault();
            newValue = num;
          }
          break;
      }

      if (newValue !== value) {
        onChange?.(newValue);
      }
    };

    /**
     * Render individual icon
     */
    const renderIcon = (index: number) => {
      const iconState = getIconState(index);
      const isFilled = iconState === "filled" || iconState === "half";
      const iconValue = index + 1;

      const iconElement =
        customIcon || customEmptyIcon ? (
          isFilled ? (
            customIcon
          ) : (
            customEmptyIcon
          )
        ) : (
          <IconComponent
            className={cn(
              iconVariants({
                variant,
                size,
                state: iconState,
                interactive: isInteractive && hoverable,
              }),
              isFilled && "fill-current",
              iconState === "half" && "opacity-70",
              disabled && "opacity-50 cursor-not-allowed",
              animated && "transition-all duration-200",
              iconClassName
            )}
            style={{
              color: isFilled ? color : emptyColor,
              ...(iconState === "half" && {
                clipPath: "polygon(0 0, 50% 0, 50% 100%, 0 100%)",
              }),
            }}
          />
        );

      return (
        <span
          key={index}
          role="radio"
          aria-checked={value >= iconValue}
          aria-label={
            tooltipLabels?.[index] ||
            `${iconValue} ${iconValue === 1 ? "star" : "stars"}`
          }
          tabIndex={
            isInteractive ? (focusedIndex === index ? 0 : -1) : undefined
          }
          className={cn(
            "relative inline-flex items-center justify-center",
            isInteractive && "cursor-pointer",
            disabled && "cursor-not-allowed"
          )}
          onClick={(e) => handleClick(index, e)}
          onMouseEnter={(e) => handleMouseEnter(index, e)}
          onMouseMove={(e) => handleMouseMove(index, e)}
          onFocus={() => setFocusedIndex(index)}
          onBlur={() => setFocusedIndex(null)}
          title={showTooltip ? tooltipLabels?.[index] : undefined}
        >
          {iconElement}

          {/* Half fill overlay for half stars */}
          {iconState === "half" && !customIcon && (
            <IconComponent
              className={cn(
                iconVariants({
                  variant,
                  size,
                  state: "filled",
                  interactive: false,
                }),
                "fill-current absolute top-0 left-0 pointer-events-none",
                animated && "transition-all duration-200"
              )}
              style={{
                color: color,
                clipPath: "polygon(0 0, 50% 0, 50% 100%, 0 100%)",
              }}
            />
          )}
        </span>
      );
    };

    const formattedValue = valueFormat
      ? valueFormat(value, max)
      : `${value.toFixed(precision === "half" ? 1 : 0)}`;

    return (
      <div
        ref={ref}
        className={cn(
          "inline-flex items-center gap-2",
          disabled && "opacity-50 cursor-not-allowed",
          className
        )}
        {...props}
      >
        <div
          ref={containerRef}
          role="radiogroup"
          aria-label="Rating"
          aria-valuenow={value}
          aria-valuemin={0}
          aria-valuemax={max}
          className={cn(ratingVariants({ variant, size }))}
          onMouseLeave={handleMouseLeave}
          onKeyDown={handleKeyDown}
          tabIndex={isInteractive ? 0 : undefined}
        >
          {Array.from({ length: max }, (_, i) => renderIcon(i))}
        </div>

        {/* Value Display */}
        {showValue && (
          <span className="text-sm font-medium text-foreground">
            {formattedValue}
            {max !== 5 && <span className="text-muted-foreground">/{max}</span>}
          </span>
        )}

        {/* Review Count */}
        {count !== undefined && (
          <span className="text-sm text-muted-foreground">
            ({count.toLocaleString()} {countLabel})
          </span>
        )}
      </div>
    );
  }
);

Rating.displayName = "Rating";

export default Rating;
export { ratingVariants };

"use client";

import React, { useEffect, useRef, useState } from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import { X, CheckCircle, AlertCircle, AlertTriangle, Info } from "lucide-react";
import type { SnackbarProps } from "./Snackbar.types";
import {
  snackbarVariants,
  snackbarIconVariants,
  snackbarMessageVariants,
  snackbarActionVariants,
  snackbarCloseVariants,
} from "./Snackbar.styles";

export type SnackbarVariantsProps = VariantProps<typeof snackbarVariants>;

/**
 * Get default icon based on severity
 */
const getDefaultIcon = (severity: string) => {
  switch (severity) {
    case "success":
      return <CheckCircle />;
    case "warning":
      return <AlertTriangle />;
    case "error":
      return <AlertCircle />;
    case "info":
      return <Info />;
    default:
      return null;
  }
};

/**
 * Snackbar Component
 *
 * A brief notification message that appears at the edge of the screen.
 * Provides lightweight feedback about an operation without interrupting the user.
 * Auto-dismisses after a specified duration.
 *
 * @example
 * ```tsx
 * // Basic snackbar
 * <Snackbar open message="Item saved successfully" />
 *
 * // With severity
 * <Snackbar
 *   open
 *   message="Action completed"
 *   severity="success"
 * />
 *
 * // With action button
 * <Snackbar
 *   open
 *   message="Item deleted"
 *   action={
 *     <button className="text-sm font-medium">UNDO</button>
 *   }
 * />
 *
 * // Auto-hide with callback
 * <Snackbar
 *   open={open}
 *   message="Notification"
 *   autoHideDuration={3000}
 *   onClose={() => setOpen(false)}
 * />
 *
 * // Custom position
 * <Snackbar
 *   open
 *   message="Message"
 *   position="top-right"
 * />
 *
 * // Different variants
 * <Snackbar open message="Filled" variant="filled" severity="success" />
 * <Snackbar open message="Outlined" variant="outlined" severity="info" />
 * <Snackbar open message="Soft" variant="soft" severity="warning" />
 * ```
 */
const Snackbar = React.forwardRef<HTMLDivElement, SnackbarProps>(
  (
    {
      open,
      message,
      variant = "filled",
      severity = "default",
      position = "bottom-center",
      action,
      icon,
      showIcon = true,
      showClose = true,
      autoHideDuration = 6000,
      onClose,
      transitionDuration = 300,
      disableClickAway = false,
      className,
      messageClassName,
      actionClassName,
      zIndex = 1400,
      pauseOnHover = true,
      pauseOnFocusLoss = true,
      onEnter,
      onEntered,
      onExit,
      onExited,
      style,
      ...props
    },
    ref,
  ) => {
    const [isHovered, setIsHovered] = useState(false);
    const [isFocused, setIsFocused] = useState(false);
    const timerRef = useRef<NodeJS.Timeout | null>(null);

    // Handle auto-hide
    useEffect(() => {
      if (!open || autoHideDuration === null) return;

      // Clear existing timer
      if (timerRef.current) {
        clearTimeout(timerRef.current);
      }

      // Don't start timer if paused
      if ((pauseOnHover && isHovered) || (pauseOnFocusLoss && isFocused)) {
        return;
      }

      // Set new timer
      timerRef.current = setTimeout(() => {
        handleClose(null, "timeout");
      }, autoHideDuration);

      return () => {
        if (timerRef.current) {
          clearTimeout(timerRef.current);
        }
      };
    }, [open, autoHideDuration, isHovered, isFocused, pauseOnHover, pauseOnFocusLoss]);

    // Handle enter/exit callbacks
    useEffect(() => {
      if (open) {
        onEnter?.();
        const timer = setTimeout(() => {
          onEntered?.();
        }, transitionDuration);
        return () => clearTimeout(timer);
      } else {
        onExit?.();
        const timer = setTimeout(() => {
          onExited?.();
        }, transitionDuration);
        return () => clearTimeout(timer);
      }
    }, [open, transitionDuration, onEnter, onEntered, onExit, onExited]);

    const handleClose = (event: React.SyntheticEvent | Event | null, reason?: string) => {
      if (reason === "clickaway" && disableClickAway) {
        return;
      }

      onClose?.(event || undefined, reason);
    };

    const handleCloseClick = (event: React.MouseEvent<HTMLButtonElement>) => {
      event.stopPropagation();
      handleClose(event, "closeButton");
    };

    // Get icon to display
    const displayIcon = icon || (showIcon && severity !== "default" ? getDefaultIcon(severity) : null);

    // Don't render if not open (after animation)
    if (!open) {
      return null;
    }

    return (
      <div
        ref={ref}
        className={cn(
          snackbarVariants({
            variant,
            severity,
            position,
            open,
          }),
          className,
        )}
        style={{ zIndex, ...style }}
        role="alert"
        aria-live="polite"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onFocus={() => setIsFocused(true)}
        onBlur={() => setIsFocused(false)}
        {...props}
      >
        {/* Icon */}
        {displayIcon && (
          <div className={cn(snackbarIconVariants({ severity }))}>
            {displayIcon}
          </div>
        )}

        {/* Message */}
        <div className={cn(snackbarMessageVariants({ variant }), messageClassName)}>
          {message}
        </div>

        {/* Action */}
        {action && (
          <div className={cn(snackbarActionVariants({ variant }), actionClassName)}>
            {action}
          </div>
        )}

        {/* Close Button */}
        {showClose && (
          <button
            type="button"
            className={cn(snackbarCloseVariants({ variant }))}
            onClick={handleCloseClick}
            aria-label="Close notification"
          >
            <X className="h-4 w-4" />
          </button>
        )}
      </div>
    );
  },
);

Snackbar.displayName = "Snackbar";

export { Snackbar };
export default Snackbar;
export type { SnackbarProps };

"use client";

import * as React from "react";
import { cn } from "../../lib/utils";
import {
  sonnerToastVariants,
  sonnerContainerVariants,
  sonnerIconVariants,
} from "./Sonner.styles";
import {
  CheckCircle2,
  XCircle,
  AlertCircle,
  Info,
  X,
  Loader2,
} from "lucide-react";
import type {
  SonnerVariant,
  SonnerPosition,
  SonnerType,
  Toast,
  SonnerProviderProps,
  SonnerContextValue,
  ToastItemProps,
} from "./Sonner.types";

export type {
  SonnerVariant,
  SonnerPosition,
  SonnerType,
  Toast,
  SonnerProviderProps,
  SonnerContextValue,
  ToastItemProps,
};

// ============================================================================
// CONTEXT
// ============================================================================

const SonnerContext = React.createContext<SonnerContextValue | undefined>(
  undefined,
);

export function useSonner() {
  const context = React.useContext(SonnerContext);
  if (!context) {
    throw new Error("useSonner must be used within SonnerProvider");
  }
  return context;
}

// ============================================================================
// SONNER PROVIDER
// ============================================================================

export function SonnerProvider({
  children,
  position = "bottom-right",
  duration = 4000,
  maxToasts = 3,
  variant = "default",
  expand = false,
  richColors = true,
}: SonnerProviderProps) {
  const [toasts, setToasts] = React.useState<Toast[]>([]);

  const toast = React.useCallback(
    (newToast: Omit<Toast, "id">): string => {
      const id = Math.random().toString(36).substring(2, 9);
      const toastWithId: Toast = {
        ...newToast,
        id,
        variant: newToast.variant || variant,
        duration: newToast.duration || duration,
      };

      setToasts((prev) => {
        const updated = [toastWithId, ...prev];
        return updated.slice(0, maxToasts);
      });

      if (
        toastWithId.duration &&
        toastWithId.duration > 0 &&
        toastWithId.type !== "loading"
      ) {
        setTimeout(() => {
          dismiss(id);
        }, toastWithId.duration);
      }

      return id;
    },
    [variant, duration, maxToasts],
  );

  const success = React.useCallback(
    (title: string, description?: string) => {
      return toast({ type: "success", title, description });
    },
    [toast],
  );

  const error = React.useCallback(
    (title: string, description?: string) => {
      return toast({ type: "error", title, description });
    },
    [toast],
  );

  const warning = React.useCallback(
    (title: string, description?: string) => {
      return toast({ type: "warning", title, description });
    },
    [toast],
  );

  const info = React.useCallback(
    (title: string, description?: string) => {
      return toast({ type: "info", title, description });
    },
    [toast],
  );

  const loading = React.useCallback(
    (title: string, description?: string) => {
      return toast({ type: "loading", title, description, duration: 0 });
    },
    [toast],
  );

  const dismiss = React.useCallback((id: string) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  }, []);

  const dismissAll = React.useCallback(() => {
    setToasts([]);
  }, []);

  const update = React.useCallback((id: string, toast: Partial<Toast>) => {
    setToasts((prev) =>
      prev.map((t) => (t.id === id ? { ...t, ...toast } : t)),
    );
  }, []);

  const contextValue: SonnerContextValue = {
    toasts,
    toast,
    success,
    error,
    warning,
    info,
    loading,
    dismiss,
    dismissAll,
    update,
  };

  return (
    <SonnerContext.Provider value={contextValue}>
      {children}
      <SonnerContainer
        toasts={toasts}
        position={position}
        expand={expand}
        richColors={richColors}
        onDismiss={dismiss}
      />
    </SonnerContext.Provider>
  );
}

// ============================================================================
// SONNER CONTAINER
// ============================================================================

interface SonnerContainerProps {
  toasts: Toast[];
  position: SonnerPosition;
  expand: boolean;
  richColors: boolean;
  onDismiss: (id: string) => void;
}

function SonnerContainer({
  toasts,
  position,
  expand,
  richColors,
  onDismiss,
}: SonnerContainerProps) {
  if (toasts.length === 0) return null;

  return (
    <div
      data-slot="sonner-container"
      className={cn(sonnerContainerVariants({ position }))}
    >
      {toasts.map((toast, index) => (
        <SonnerToast
          key={toast.id}
          toast={toast}
          index={index}
          expand={expand}
          richColors={richColors}
          onDismiss={onDismiss}
        />
      ))}
    </div>
  );
}

// ============================================================================
// SONNER TOAST
// ============================================================================

interface SonnerToastProps {
  toast: Toast;
  index: number;
  expand: boolean;
  richColors: boolean;
  onDismiss: (id: string) => void;
}

function SonnerToast({
  toast,
  index,
  expand,
  richColors,
  onDismiss,
}: SonnerToastProps) {
  const [isExiting, setIsExiting] = React.useState(false);
  const [isHovered, setIsHovered] = React.useState(false);

  const handleDismiss = () => {
    setIsExiting(true);
    setTimeout(() => {
      onDismiss(toast.id);
      toast.onDismiss?.();
    }, 200);
  };

  const getIcon = () => {
    switch (toast.type) {
      case "success":
        return <CheckCircle2 className="h-5 w-5" />;
      case "error":
        return <XCircle className="h-5 w-5" />;
      case "warning":
        return <AlertCircle className="h-5 w-5" />;
      case "info":
        return <Info className="h-5 w-5" />;
      case "loading":
        return <Loader2 className="h-5 w-5 animate-spin" />;
      default:
        return null;
    }
  };

  const getTypeVariant = (): SonnerVariant => {
    if (!richColors) return toast.variant || "default";

    switch (toast.type) {
      case "success":
        return "success";
      case "error":
        return "error";
      case "warning":
        return "warning";
      case "info":
        return "info";
      default:
        return toast.variant || "default";
    }
  };

  return (
    <div
      data-slot="sonner-toast"
      className={cn(
        sonnerToastVariants({ variant: getTypeVariant() }),
        "transform transition-all duration-200",
        isExiting && "opacity-0 scale-95 translate-x-full",
        !expand && index > 0 && "mt-2 scale-95 opacity-80",
        expand && "mt-2",
        isHovered && "scale-100 opacity-100",
      )}
      style={{
        zIndex: 1000 - index,
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="flex items-start gap-3">
        {/* Icon */}
        {toast.type !== "default" && (
          <div className={cn(sonnerIconVariants({ type: toast.type }))}>
            {getIcon()}
          </div>
        )}

        {/* Content */}
        <div className="flex-1 space-y-1">
          <div className="font-semibold text-sm">{toast.title}</div>
          {toast.description && (
            <div className="text-sm opacity-90">{toast.description}</div>
          )}

          {/* Actions */}
          {(toast.action || toast.cancel) && (
            <div className="flex items-center gap-2 mt-3">
              {toast.action && (
                <button
                  onClick={() => {
                    toast.action!.onClick();
                    handleDismiss();
                  }}
                  className="px-3 py-1.5 text-xs font-medium rounded-md bg-foreground/10 hover:bg-foreground/20 transition-colors"
                >
                  {toast.action.label}
                </button>
              )}
              {toast.cancel && (
                <button
                  onClick={() => {
                    toast.cancel!.onClick?.();
                    handleDismiss();
                  }}
                  className="px-3 py-1.5 text-xs font-medium rounded-md hover:bg-foreground/10 transition-colors"
                >
                  {toast.cancel.label}
                </button>
              )}
            </div>
          )}
        </div>

        {/* Dismiss button */}
        {toast.dismissible !== false && (
          <button
            onClick={handleDismiss}
            className="opacity-70 hover:opacity-100 transition-opacity"
          >
            <X className="h-4 w-4" />
          </button>
        )}
      </div>
    </div>
  );
}

// ============================================================================
// COMPACT API
// ============================================================================

export interface SonnerProps {
  position?: SonnerPosition;
  duration?: number;
  maxToasts?: number;
  variant?: SonnerVariant;
  expand?: boolean;
  richColors?: boolean;
}

export function Sonner(props: SonnerProps) {
  return <SonnerProvider {...props}>{null}</SonnerProvider>;
}

