"use client";

import React, { useState, useRef, useEffect } from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import { X } from "lucide-react";
import type { SpeedDialProps, SpeedDialAction } from "./SpeedDial.types";
import {
  speedDialVariants,
  speedDialButtonVariants,
  speedDialActionsVariants,
  speedDialActionVariants,
  speedDialActionLabelVariants,
  speedDialBackdropVariants,
} from "./SpeedDial.styles";

export type SpeedDialVariantsProps = VariantProps<typeof speedDialVariants>;

/**
 * SpeedDial Component
 *
 * A floating action button that expands to reveal multiple action buttons.
 * Perfect for providing quick access to common actions in your application.
 * Supports multiple positions, directions, and customizable actions.
 *
 * @example
 * ```tsx
 * // Basic speed dial
 * <SpeedDial
 *   icon={<Plus />}
 *   actions={[
 *     { id: '1', icon: <Edit />, label: 'Edit', onClick: handleEdit },
 *     { id: '2', icon: <Share />, label: 'Share', onClick: handleShare },
 *     { id: '3', icon: <Delete />, label: 'Delete', onClick: handleDelete },
 *   ]}
 * />
 *
 * // With custom position and direction
 * <SpeedDial
 *   icon={<Menu />}
 *   position="bottom-right"
 *   direction="up"
 *   actions={actions}
 *   aria-label="Quick actions"
 * />
 *
 * // With custom colors
 * <SpeedDial
 *   icon={<Add />}
 *   color="primary"
 *   actions={[
 *     { id: '1', icon: <File />, label: 'New File', color: 'primary' },
 *     { id: '2', icon: <Folder />, label: 'New Folder', color: 'secondary' },
 *   ]}
 * />
 *
 * // Controlled mode
 * const [isOpen, setIsOpen] = useState(false);
 * <SpeedDial
 *   icon={<Plus />}
 *   open={isOpen}
 *   onOpenChange={setIsOpen}
 *   actions={actions}
 * />
 *
 * // With backdrop
 * <SpeedDial
 *   icon={<Plus />}
 *   actions={actions}
 *   showBackdrop={true}
 * />
 * ```
 */
const SpeedDial = React.forwardRef<HTMLDivElement, SpeedDialProps>(
  (
    {
      icon,
      actions,
      position = "bottom-right",
      direction = "up",
      color = "primary",
      size = "md",
      open: controlledOpen,
      onOpenChange,
      showLabels = true,
      closeOnActionClick = true,
      closeOnClickOutside = true,
      disabled = false,
      openIcon,
      className,
      buttonClassName,
      actionsClassName,
      actionClassName,
      showBackdrop = false,
      backdropClassName,
      "aria-label": ariaLabel = "Speed dial actions",
      ...props
    },
    ref,
  ) => {
    const [uncontrolledOpen, setUncontrolledOpen] = useState(false);
    const [hoveredAction, setHoveredAction] = useState<string | null>(null);
    const containerRef = useRef<HTMLDivElement>(null);

    // Determine if component is controlled
    const isControlled = controlledOpen !== undefined;
    const isOpen = isControlled ? controlledOpen : uncontrolledOpen;

    // Set open state
    const setIsOpen = (newOpen: boolean) => {
      if (!isControlled) {
        setUncontrolledOpen(newOpen);
      }
      onOpenChange?.(newOpen);
    };

    // Toggle open state
    const toggleOpen = () => {
      if (!disabled) {
        setIsOpen(!isOpen);
      }
    };

    // Handle action click
    const handleActionClick = (
      action: SpeedDialAction,
      event: React.MouseEvent<HTMLButtonElement>,
    ) => {
      if (action.disabled) return;

      action.onClick?.(event);

      if (closeOnActionClick) {
        setIsOpen(false);
      }
    };

    // Handle click outside
    useEffect(() => {
      if (!closeOnClickOutside || !isOpen) return;

      const handleClickOutside = (event: MouseEvent) => {
        if (
          containerRef.current &&
          !containerRef.current.contains(event.target as Node)
        ) {
          setIsOpen(false);
        }
      };

      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [isOpen, closeOnClickOutside]);

    // Handle escape key
    useEffect(() => {
      if (!isOpen) return;

      const handleEscape = (event: KeyboardEvent) => {
        if (event.key === "Escape") {
          setIsOpen(false);
        }
      };

      document.addEventListener("keydown", handleEscape);
      return () => {
        document.removeEventListener("keydown", handleEscape);
      };
    }, [isOpen]);

    // Determine label position based on direction
    const getLabelDirection = () => {
      switch (direction) {
        case "up":
        case "down":
          return "left";
        case "left":
          return "left";
        case "right":
          return "right";
        default:
          return "left";
      }
    };

    const labelDirection = getLabelDirection();

    return (
      <>
        {/* Backdrop */}
        {showBackdrop && (
          <div
            className={cn(
              speedDialBackdropVariants({ open: isOpen }),
              backdropClassName,
            )}
            onClick={() => setIsOpen(false)}
            aria-hidden="true"
          />
        )}

        {/* SpeedDial Container */}
        <div
          ref={(node) => {
            // Handle both refs
            if (typeof ref === "function") {
              ref(node);
            } else if (ref) {
              ref.current = node;
            }
            (
              containerRef as React.MutableRefObject<HTMLDivElement | null>
            ).current = node;
          }}
          className={cn(speedDialVariants({ position }), className)}
          {...props}
        >
          {/* Actions */}
          <div
            className={cn(
              speedDialActionsVariants({ direction, open: isOpen }),
              actionsClassName,
            )}
            role="menu"
            aria-orientation={
              direction === "up" || direction === "down"
                ? "vertical"
                : "horizontal"
            }
          >
            {actions.map((action: SpeedDialAction, index: number) => (
              <div
                key={action.id}
                className="relative"
                style={{
                  transitionDelay: isOpen
                    ? `${index * 50}ms`
                    : `${(actions.length - index - 1) * 30}ms`,
                }}
              >
                <button
                  type="button"
                  onClick={(e) => handleActionClick(action, e)}
                  disabled={action.disabled}
                  className={cn(
                    speedDialActionVariants({
                      color: action.color || "default",
                    }),
                    action.className,
                    actionClassName,
                  )}
                  onMouseEnter={() => setHoveredAction(action.id)}
                  onMouseLeave={() => setHoveredAction(null)}
                  onFocus={() => setHoveredAction(action.id)}
                  onBlur={() => setHoveredAction(null)}
                  aria-label={action.label}
                  role="menuitem"
                >
                  {action.icon}
                </button>

                {/* Label/Tooltip */}
                {showLabels && action.label && (
                  <span
                    className={cn(
                      speedDialActionLabelVariants({
                        direction: labelDirection,
                        show: hoveredAction === action.id,
                      }),
                    )}
                    aria-hidden="true"
                  >
                    {action.label}
                  </span>
                )}
              </div>
            ))}
          </div>

          {/* Main FAB Button */}
          <button
            type="button"
            onClick={toggleOpen}
            disabled={disabled}
            className={cn(
              speedDialButtonVariants({ color, size, open: isOpen }),
              buttonClassName,
            )}
            aria-label={ariaLabel}
            aria-expanded={isOpen}
            aria-haspopup="menu"
          >
            {isOpen && openIcon ? (
              openIcon
            ) : isOpen ? (
              <X className="h-6 w-6" />
            ) : (
              icon
            )}
          </button>
        </div>
      </>
    );
  },
);

SpeedDial.displayName = "SpeedDial";

export { SpeedDial };
export default SpeedDial;
export type { SpeedDialProps, SpeedDialAction };

import React from "react";
import { cn } from "../../lib/utils";
import { SpinnerProps, SpinnerSize } from "./Spinner.types";
import {
  containerVariants,
  labelVariants,
  logoSizeVariants,
  spinnerVariants,
} from "./Spinner.styles";

/**
 * Animation variants for different animation types
 */
const animationVariants = {
  spin: "animate-spin",
  pulse: "animate-pulse",
  bounce: "animate-bounce",
  ping: "animate-ping",
};

/**
 * Get color classes based on variant
 */
const getColorClasses = (variant: string) => {
  const colorMap: Record<string, string> = {
    default: "bg-foreground/80",
    primary: "bg-primary",
    secondary: "bg-secondary",
    accent: "bg-accent",
    info: "bg-info",
    success: "bg-success",
    warning: "bg-warning",
    error: "bg-destructive",
    glass: "bg-primary/60",
    gradient: "bg-gradient-to-r from-primary via-accent to-secondary",
  };
  return colorMap[variant] || colorMap.primary;
};

/**
 * Get size dimensions for spinners
 */
const getSizeClasses = (size: SpinnerSize = "md") => {
  const sizeMap = {
    xs: "w-4 h-4",
    sm: "w-6 h-6",
    md: "w-8 h-8",
    lg: "w-12 h-12",
    xl: "w-16 h-16",
    "2xl": "w-32 h-32",
  };
  return sizeMap[size] || sizeMap.md;
};

/**
 * Get dot/bar size for different spinner types
 */
const getDotSize = (size: SpinnerSize = "md") => {
  const dotMap = {
    xs: "w-1 h-1",
    sm: "w-1.5 h-1.5",
    md: "w-2 h-2",
    lg: "w-3 h-3",
    xl: "w-4 h-4",
    "2xl": "w-8 h-8",
  };
  return dotMap[size] || dotMap.md;
};

/**
 * Spinner Component
 *
 * A versatile loading spinner with multiple variants, sizes, and animations.
 * Perfect for indicating loading states with modern effects.
 *
 * @example
 * ```tsx
 * <Spinner />
 * <Spinner variant="success" size="lg" label="Loading..." />
 * <Spinner variant="gradient" animation="pulse" fullscreen />
 * <Spinner logo="/logo.png" variant="primary" size="xl" />
 * <Spinner logo={<YourIcon />} variant="success" />
 * <Spinner type="dots" variant="primary" />
 * <Spinner type="bars" variant="accent" size="lg" />
 * ```
 */
const Spinner = React.forwardRef<HTMLDivElement, SpinnerProps>(
  (
    {
      variant = "primary",
      size = "md",
      animation = "spin",
      type = "ring",
      label,
      fullscreen = false,
      speed = 1,
      thickness = "default",
      logo,
      logoSize = "md",
      className,
      ...props
    },
    ref
  ) => {
    const animationClass = animationVariants[animation];
    const animationStyle =
      speed !== 1 ? { animationDuration: `${1 / speed}s` } : {};
    const colorClass = getColorClasses(variant);
    const sizeClass = getSizeClasses(size);
    const dotSizeClass = getDotSize(size);

    const renderSpinner = () => {
      switch (type) {
        case "dots": {
          // Rotating dots in arc pattern (like iOS spinner)
          const dots = 8;
          const radius =
            size === "xs"
              ? 6
              : size === "sm"
              ? 10
              : size === "md"
              ? 16
              : size === "lg"
              ? 28
              : size === "xl"
              ? 44
              : 60;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: dots }).map((_, i) => {
                const angle = (i * 360) / dots;
                const radian = (angle * Math.PI) / 180;
                const x = Math.cos(radian) * radius;
                const y = Math.sin(radian) * radius;

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2",
                      dotSizeClass,
                      colorClass,
                      "rounded-full"
                    )}
                    style={{
                      transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px))`,
                      opacity: 1 - i * 0.1,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "dashed": {
          // Dashed circular border
          return (
            <div
              className={cn(
                spinnerVariants({ variant, size, thickness }),
                animationClass,
                "border-dashed"
              )}
              style={animationStyle}
              aria-hidden="true"
            />
          );
        }

        case "bars": {
          // Rotating bars radiating from center
          const bars = 8;
          const barHeight =
            size === "xs"
              ? 8
              : size === "sm"
              ? 12
              : size === "md"
              ? 16
              : size === "lg"
              ? 24
              : size === "xl"
              ? 32
              : 48;
          const barWidth =
            size === "xs"
              ? 2
              : size === "sm"
              ? 2
              : size === "md"
              ? 4
              : size === "lg"
              ? 4
              : size === "xl"
              ? 6
              : 8;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: bars }).map((_, i) => {
                const angle = (i * 360) / bars;

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2 rounded-full",
                      colorClass
                    )}
                    style={{
                      width: `${barWidth}px`,
                      height: `${barHeight}px`,
                      transform: `translate(-50%, -50%) rotate(${angle}deg) translateY(-${
                        barHeight / 2
                      }px)`,
                      transformOrigin: "center center",
                      opacity: 1 - i * 0.1,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "dotRing": {
          // Complete circle of evenly-spaced dots
          const dots = 12;
          const radius =
            size === "xs"
              ? 6
              : size === "sm"
              ? 10
              : size === "md"
              ? 14
              : size === "lg"
              ? 20
              : size === "xl"
              ? 28
              : 56;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: dots }).map((_, i) => {
                const angle = (i * 360) / dots;
                const radian = (angle * Math.PI) / 180;
                const x = Math.cos(radian) * radius;
                const y = Math.sin(radian) * radius;

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2",
                      dotSizeClass,
                      colorClass,
                      "rounded-full"
                    )}
                    style={{
                      transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px))`,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "orbit": {
          // Orbiting dots with different orbit sizes
          const orbits = 3;
          const dotsPerOrbit = 4;
          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: orbits }).map((_, orbitIndex) => {
                const radius =
                  (orbitIndex + 1) *
                  (size === "xs"
                    ? 4
                    : size === "sm"
                    ? 6
                    : size === "md"
                    ? 10
                    : size === "lg"
                    ? 14
                    : size === "xl"
                    ? 18
                    : 24);

                return Array.from({ length: dotsPerOrbit }).map(
                  (_, dotIndex) => {
                    const angle =
                      (dotIndex * 360) / dotsPerOrbit + orbitIndex * 45;
                    const radian = (angle * Math.PI) / 180;
                    const x = Math.cos(radian) * radius;
                    const y = Math.sin(radian) * radius;

                    return (
                      <div
                        key={`${orbitIndex}-${dotIndex}`}
                        className={cn(
                          "absolute top-1/2 left-1/2",
                          dotSizeClass,
                          colorClass,
                          "rounded-full"
                        )}
                        style={{
                          transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px))`,
                          opacity: 1 - orbitIndex * 0.2,
                        }}
                      />
                    );
                  }
                );
              })}
            </div>
          );
        }

        case "pulse": {
          // Pulsing concentric circles
          const circles = 3;

          return (
            <div className={cn(sizeClass, "relative")} aria-hidden="true">
              {Array.from({ length: circles }).map((_, i) => (
                <div
                  key={i}
                  className={cn(
                    "absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full border-4",
                    colorClass.replace("bg-", "border-")
                  )}
                  style={{
                    width: `${100 - i * 20}%`,
                    height: `${100 - i * 20}%`,
                    animation: `ping ${
                      2 + i * 0.5
                    }s cubic-bezier(0, 0, 0.2, 1) infinite`,
                    animationDelay: `${i * 0.3}s`,
                    opacity: 0.6 - i * 0.15,
                  }}
                />
              ))}
            </div>
          );
        }

        case "square": {
          // Rotating square corners
          const corners = 4;
          const cornerSize =
            size === "xs"
              ? 3
              : size === "sm"
              ? 4
              : size === "md"
              ? 6
              : size === "lg"
              ? 8
              : size === "xl"
              ? 10
              : 12;
          const offset =
            size === "xs"
              ? 6
              : size === "sm"
              ? 10
              : size === "md"
              ? 16
              : size === "lg"
              ? 24
              : size === "xl"
              ? 32
              : 48;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: corners }).map((_, i) => {
                const positions = [
                  { x: offset, y: -offset },
                  { x: offset, y: offset },
                  { x: -offset, y: offset },
                  { x: -offset, y: -offset },
                ];
                const pos = positions[i];

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2 rounded-sm",
                      colorClass
                    )}
                    style={{
                      width: `${cornerSize}px`,
                      height: `${cornerSize}px`,
                      transform: `translate(calc(-50% + ${pos.x}px), calc(-50% + ${pos.y}px))`,
                      opacity: 1 - i * 0.2,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "triangle": {
          // Rotating triangular pattern
          const points = 3;
          const radius =
            size === "xs"
              ? 8
              : size === "sm"
              ? 12
              : size === "md"
              ? 18
              : size === "lg"
              ? 26
              : size === "xl"
              ? 36
              : 54;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: points }).map((_, i) => {
                const angle = (i * 360) / points - 90;
                const radian = (angle * Math.PI) / 180;
                const x = Math.cos(radian) * radius;
                const y = Math.sin(radian) * radius;

                return (
                  <div
                    key={i}
                    className={cn("absolute top-1/2 left-1/2", colorClass)}
                    style={{
                      width: 0,
                      height: 0,
                      borderLeft: `${
                        size === "xs"
                          ? 4
                          : size === "sm"
                          ? 6
                          : size === "md"
                          ? 8
                          : size === "lg"
                          ? 10
                          : size === "xl"
                          ? 12
                          : 16
                      }px solid transparent`,
                      borderRight: `${
                        size === "xs"
                          ? 4
                          : size === "sm"
                          ? 6
                          : size === "md"
                          ? 8
                          : size === "lg"
                          ? 10
                          : size === "xl"
                          ? 12
                          : 16
                      }px solid transparent`,
                      borderBottom: `${
                        size === "xs"
                          ? 6
                          : size === "sm"
                          ? 10
                          : size === "md"
                          ? 14
                          : size === "lg"
                          ? 18
                          : size === "xl"
                          ? 22
                          : 28
                      }px solid currentColor`,
                      transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px)) rotate(${
                        i * 120
                      }deg)`,
                      opacity: 1 - i * 0.25,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "wave": {
          // Wave-like bars with sequential animation
          const bars = 5;
          const barHeight =
            size === "xs"
              ? 12
              : size === "sm"
              ? 16
              : size === "md"
              ? 24
              : size === "lg"
              ? 32
              : size === "xl"
              ? 40
              : 56;
          const barWidth =
            size === "xs"
              ? 2
              : size === "sm"
              ? 3
              : size === "md"
              ? 4
              : size === "lg"
              ? 5
              : size === "xl"
              ? 6
              : 8;

          return (
            <div
              className={cn(
                sizeClass,
                "relative flex items-center justify-center gap-1"
              )}
              aria-hidden="true"
            >
              {Array.from({ length: bars }).map((_, i) => (
                <div
                  key={i}
                  className={cn("rounded-full", colorClass)}
                  style={{
                    width: `${barWidth}px`,
                    height: `${barHeight}px`,
                    animation: `bounce 1s ease-in-out infinite`,
                    animationDelay: `${i * 0.1}s`,
                  }}
                />
              ))}
            </div>
          );
        }

        case "spiral": {
          // Spiral rotating pattern
          const dots = 12;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: dots }).map((_, i) => {
                const angle = (i * 360) / dots;
                const radian = (angle * Math.PI) / 180;
                const spiralRadius =
                  (i / dots) *
                  (size === "xs"
                    ? 8
                    : size === "sm"
                    ? 12
                    : size === "md"
                    ? 18
                    : size === "lg"
                    ? 26
                    : size === "xl"
                    ? 36
                    : 54);
                const x = Math.cos(radian) * spiralRadius;
                const y = Math.sin(radian) * spiralRadius;

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2",
                      dotSizeClass,
                      colorClass,
                      "rounded-full"
                    )}
                    style={{
                      transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px))`,
                      opacity: (i / dots) * 0.8 + 0.2,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "infinity": {
          // Infinity symbol pattern
          const dots = 20;
          const scale =
            size === "xs"
              ? 0.3
              : size === "sm"
              ? 0.5
              : size === "md"
              ? 0.7
              : size === "lg"
              ? 1
              : size === "xl"
              ? 1.3
              : 1.8;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: dots }).map((_, i) => {
                const t = (i / dots) * Math.PI * 2;
                const x =
                  ((20 * Math.cos(t)) / (1 + Math.sin(t) * Math.sin(t))) *
                  scale;
                const y =
                  ((20 * Math.cos(t) * Math.sin(t)) /
                    (1 + Math.sin(t) * Math.sin(t))) *
                  scale;

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2",
                      dotSizeClass,
                      colorClass,
                      "rounded-full"
                    )}
                    style={{
                      transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px))`,
                      opacity: 1 - (i / dots) * 0.5,
                    }}
                  />
                );
              })}
            </div>
          );
        }

        case "flower": {
          // Flower petal pattern
          const petals = 6;
          const radius =
            size === "xs"
              ? 8
              : size === "sm"
              ? 12
              : size === "md"
              ? 18
              : size === "lg"
              ? 26
              : size === "xl"
              ? 36
              : 54;

          return (
            <div
              className={cn(sizeClass, "relative", animationClass)}
              style={animationStyle}
              aria-hidden="true"
            >
              {Array.from({ length: petals }).map((_, i) => {
                const angle = (i * 360) / petals;
                const radian = (angle * Math.PI) / 180;
                const x = Math.cos(radian) * radius;
                const y = Math.sin(radian) * radius;

                return (
                  <div
                    key={i}
                    className={cn(
                      "absolute top-1/2 left-1/2",
                      colorClass,
                      "rounded-full"
                    )}
                    style={{
                      width: `${
                        size === "xs"
                          ? 6
                          : size === "sm"
                          ? 10
                          : size === "md"
                          ? 14
                          : size === "lg"
                          ? 18
                          : size === "xl"
                          ? 24
                          : 32
                      }px`,
                      height: `${
                        size === "xs"
                          ? 12
                          : size === "sm"
                          ? 18
                          : size === "md"
                          ? 24
                          : size === "lg"
                          ? 30
                          : size === "xl"
                          ? 40
                          : 56
                      }px`,
                      transform: `translate(calc(-50% + ${x}px), calc(-50% + ${y}px)) rotate(${angle}deg)`,
                      opacity: 1 - i * 0.1,
                      borderRadius: "50% 50% 50% 0",
                    }}
                  />
                );
              })}
              <div
                className={cn(
                  "absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-full",
                  colorClass
                )}
                style={{
                  width: `${
                    size === "xs"
                      ? 6
                      : size === "sm"
                      ? 10
                      : size === "md"
                      ? 14
                      : size === "lg"
                      ? 18
                      : size === "xl"
                      ? 24
                      : 32
                  }px`,
                  height: `${
                    size === "xs"
                      ? 6
                      : size === "sm"
                      ? 10
                      : size === "md"
                      ? 14
                      : size === "lg"
                      ? 18
                      : size === "xl"
                      ? 24
                      : 32
                  }px`,
                }}
              />
            </div>
          );
        }

        case "grid": {
          // Grid loading pattern
          const gridSize = 3;
          const cellSize =
            size === "xs"
              ? 3
              : size === "sm"
              ? 4
              : size === "md"
              ? 6
              : size === "lg"
              ? 8
              : size === "xl"
              ? 10
              : 14;
          const gap =
            size === "xs"
              ? 1
              : size === "sm"
              ? 2
              : size === "md"
              ? 3
              : size === "lg"
              ? 4
              : size === "xl"
              ? 5
              : 6;

          return (
            <div
              className={cn(sizeClass, "relative grid gap-1")}
              style={{
                gridTemplateColumns: `repeat(${gridSize}, 1fr)`,
                gap: `${gap}px`,
              }}
              aria-hidden="true"
            >
              {Array.from({ length: gridSize * gridSize }).map((_, i) => (
                <div
                  key={i}
                  className={cn("rounded-sm", colorClass)}
                  style={{
                    width: `${cellSize}px`,
                    height: `${cellSize}px`,
                    animation: `pulse 1.5s cubic-bezier(0.4, 0, 0.6, 1) infinite`,
                    animationDelay: `${i * 0.1}s`,
                  }}
                />
              ))}
            </div>
          );
        }

        case "bounce": {
          // Bouncing dots
          const dots = 3;
          const dotSize =
            size === "xs"
              ? 4
              : size === "sm"
              ? 6
              : size === "md"
              ? 8
              : size === "lg"
              ? 12
              : size === "xl"
              ? 16
              : 20;
          const gap =
            size === "xs"
              ? 2
              : size === "sm"
              ? 3
              : size === "md"
              ? 4
              : size === "lg"
              ? 6
              : size === "xl"
              ? 8
              : 10;

          return (
            <div
              className={cn(
                sizeClass,
                "relative flex items-center justify-center"
              )}
              style={{ gap: `${gap}px` }}
              aria-hidden="true"
            >
              {Array.from({ length: dots }).map((_, i) => (
                <div
                  key={i}
                  className={cn("rounded-full", colorClass)}
                  style={{
                    width: `${dotSize}px`,
                    height: `${dotSize}px`,
                    animation: `bounce 1.4s ease-in-out infinite`,
                    animationDelay: `${i * 0.16}s`,
                  }}
                />
              ))}
            </div>
          );
        }

        case "ring":
        default: {
          // Classic circular border spinner (default)
          return (
            <div
              className={cn(
                spinnerVariants({ variant, size, thickness }),
                animationClass
              )}
              style={animationStyle}
              aria-hidden="true"
            />
          );
        }
      }
    };

    return (
      <div
        ref={ref}
        className={cn(containerVariants({ fullscreen }), className)}
        role="status"
        aria-live="polite"
        aria-label={label || "Loading"}
        {...props}
      >
        <div className="relative">
          {renderSpinner()}
          {logo && (
            <div className={cn(logoSizeVariants({ logoSize }))}>
              {typeof logo === "string" ? (
                <img src={logo} alt="Loading logo" className="object-contain" />
              ) : (
                logo
              )}
            </div>
          )}
        </div>
        {label && (
          <span className={cn(labelVariants({ variant }))}>{label}</span>
        )}
        <span className="sr-only">{label || "Loading..."}</span>
      </div>
    );
  }
);

Spinner.displayName = "Spinner";

export default Spinner;
export { spinnerVariants };

"use client";

import React from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import { Check, AlertCircle } from "lucide-react";
import type {
  StepperProps,
  StepLabelProps,
  StepIconProps,
  StepConnectorProps,
  StepStatus,
} from "./Stepper.types";
import {
  stepperVariants,
  stepItemVariants,
  stepIconContainerVariants,
  stepLabelVariants,
  stepDescriptionVariants,
  stepConnectorVariants,
} from "./Stepper.styles";

export type StepperVariantsProps = VariantProps<typeof stepperVariants>;

/**
 * StepIcon Component
 * Renders the icon or number for each step
 */
const StepIcon: React.FC<StepIconProps> = ({
  icon,
  stepNumber,
  active: _active = false,
  completed = false,
  error = false,
  showCheckmark = true,
  className: _className,
}) => {
  // Show checkmark for completed steps
  if (completed && showCheckmark) {
    return <Check className="h-5 w-5" />;
  }

  // Show error icon
  if (error) {
    return <AlertCircle className="h-5 w-5" />;
  }

  // Show custom icon if provided
  if (icon) {
    return <>{icon}</>;
  }

  // Show step number
  return <>{stepNumber}</>;
};

/**
 * StepLabel Component
 * Renders the label and description for each step
 */
const StepLabel: React.FC<StepLabelProps> = ({
  label,
  description,
  active = false,
  completed = false,
  error = false,
  optional = false,
  className,
}) => {
  const status: StepStatus = error
    ? "error"
    : active
      ? "active"
      : completed
        ? "complete"
        : "inactive";

  return (
    <div className={className}>
      <div className="flex items-center gap-2">
        <span className={cn(stepLabelVariants({ status }))}>{label}</span>
        {optional && (
          <span className="text-xs text-muted-foreground">(Optional)</span>
        )}
      </div>
      {description && (
        <p className={cn(stepDescriptionVariants({ status }), "mt-1")}>
          {description}
        </p>
      )}
    </div>
  );
};

/**
 * StepConnector Component
 * Renders the line connecting steps
 */
const StepConnector: React.FC<
  StepConnectorProps & { color?: "primary" | "secondary" | "success" | "error" }
> = ({
  active = false,
  completed = false,
  orientation = "horizontal",
  color = "primary",
  className,
}) => {
  const status: StepStatus = active
    ? "active"
    : completed
      ? "complete"
      : "inactive";

  return (
    <div
      className={cn(
        stepConnectorVariants({
          orientation,
          status,
          color,
        }),
        className,
      )}
    />
  );
};

/**
 * Stepper Component
 *
 * A component for displaying multi-step processes and wizards.
 * Shows progress through a series of logical and numbered steps.
 *
 * @example
 * ```tsx
 * // Basic stepper
 * const steps = [
 *   { label: 'Account' },
 *   { label: 'Profile' },
 *   { label: 'Finish' }
 * ];
 * <Stepper steps={steps} activeStep={1} />
 *
 * // With descriptions
 * const stepsWithDesc = [
 *   { label: 'Account', description: 'Create your account' },
 *   { label: 'Profile', description: 'Set up your profile' },
 *   { label: 'Finish', description: 'Review and complete' }
 * ];
 * <Stepper steps={stepsWithDesc} activeStep={0} />
 *
 * // Vertical orientation
 * <Stepper
 *   orientation="vertical"
 *   steps={steps}
 *   activeStep={activeStep}
 * />
 *
 * // Interactive (clickable steps)
 * <Stepper
 *   steps={steps}
 *   activeStep={activeStep}
 *   interactive
 *   onStepClick={(index) => setActiveStep(index)}
 * />
 *
 * // With custom icons
 * const stepsWithIcons = [
 *   { label: 'Account', icon: <User /> },
 *   { label: 'Profile', icon: <Settings /> },
 *   { label: 'Finish', icon: <Check /> }
 * ];
 * <Stepper steps={stepsWithIcons} activeStep={0} />
 *
 * // With error state
 * <Stepper steps={steps} activeStep={1} error />
 * ```
 */
const Stepper = React.forwardRef<HTMLDivElement, StepperProps>(
  (
    {
      steps,
      activeStep = 0,
      variant = "default",
      orientation = "horizontal",
      interactive = false,
      showNumbers = true,
      showConnectors = true,
      error = false,
      color = "primary",
      onStepClick,
      onChange,
      className,
      stepClassName,
      connectorClassName,
      alternativeLabel = false,
      showCheckmark = true,
      ...props
    },
    ref,
  ) => {
    const handleStepClick = (stepIndex: number, step: any) => {
      if (!interactive || step.disabled) return;

      onStepClick?.(stepIndex);
      onChange?.(stepIndex);
    };

    const getStepStatus = (stepIndex: number, step: any): StepStatus => {
      // Use custom status if provided
      if (step.status) return step.status;

      // Error state
      if (error && stepIndex === activeStep) return "error";

      // Completed steps
      if (stepIndex < activeStep) return "complete";

      // Active step
      if (stepIndex === activeStep) return "active";

      // Future steps
      return "inactive";
    };

    return (
      <div
        ref={ref}
        className={cn(
          stepperVariants({
            orientation,
            variant,
          }),
          className,
        )}
        {...props}
      >
        {steps.map((step, index) => {
          const status = getStepStatus(index, step);
          const isLast = index === steps.length - 1;
          const isClickable = interactive && !step.disabled;

          return (
            <React.Fragment key={step.id || index}>
              {/* Step Item */}
              <div
                className={cn(
                  stepItemVariants({
                    orientation,
                    interactive: isClickable,
                  }),
                  stepClassName,
                )}
                onClick={() => handleStepClick(index, step)}
                role={isClickable ? "button" : undefined}
                tabIndex={isClickable ? 0 : undefined}
                onKeyDown={(e) => {
                  if (isClickable && (e.key === "Enter" || e.key === " ")) {
                    e.preventDefault();
                    handleStepClick(index, step);
                  }
                }}
              >
                {/* Icon and Label Container */}
                <div
                  className={cn(
                    "flex items-start",
                    orientation === "horizontal"
                      ? "flex-col items-center"
                      : "flex-row w-full",
                  )}
                >
                  {/* Step Icon */}
                  <div
                    className={cn(
                      stepIconContainerVariants({
                        variant,
                        status,
                        color,
                        size: "md",
                      }),
                      step.disabled && "opacity-50 cursor-not-allowed",
                    )}
                  >
                    <StepIcon
                      icon={step.icon}
                      stepNumber={showNumbers ? index + 1 : index}
                      active={status === "active"}
                      completed={status === "complete"}
                      error={status === "error"}
                      showCheckmark={showCheckmark}
                    />
                  </div>

                  {/* Step Label */}
                  <StepLabel
                    label={step.label}
                    description={step.description}
                    active={status === "active"}
                    completed={status === "complete"}
                    error={status === "error"}
                    optional={step.optional}
                    className={cn(
                      orientation === "horizontal" ? "mt-2" : "ml-4 flex-1",
                      step.disabled && "opacity-50",
                    )}
                  />
                </div>
              </div>

              {/* Connector */}
              {showConnectors && !isLast && (
                <StepConnector
                  active={status === "active" || status === "complete"}
                  completed={index < activeStep - 1}
                  orientation={orientation}
                  color={color}
                  className={connectorClassName}
                />
              )}
            </React.Fragment>
          );
        })}
      </div>
    );
  },
);

