/**
 * TextEditor Component
 *
 * An advanced rich text editor with toolbar for formatting text.
 * Supports headings, lists, links, images, alignment, and more.
 *
 * @example
 * ```tsx
 * // Basic usage
 * <TextEditor
 *   placeholder="Start writing..."
 *   onChange={(html) => console.log(html)}
 * />
 *
 * // With variants and custom toolbar
 * <TextEditor
 *   variant="primary"
 *   size="lg"
 *   defaultValue="<p>Hello world!</p>"
 *   stickyToolbar
 * />
 *
 * // Read-only mode
 * <TextEditor
 *   value={content}
 *   readOnly
 *   showToolbar={false}
 * />
 * ```
 */
export const TextEditor = React.forwardRef<HTMLDivElement, TextEditorProps>(
  (
    {
      className,
      variant = "default",
      size = "default",
      fullWidth = true,
      defaultValue = "",
      value: controlledValue,
      onChange,
      placeholder = "Start writing...",
      disabled = false,
      readOnly = false,
      showToolbar = true,
      stickyToolbar = false,
      toolbarVariant = "default",
      toolbarButtons = defaultToolbarButtons,
      minHeight,
      maxHeight,
      autoFocus = false,
      ...props
    },
    ref,
  ) => {
    const editorRef = React.useRef<HTMLDivElement>(null);
    const [internalValue, setInternalValue] = React.useState(defaultValue);
    const [activeCommands, setActiveCommands] = React.useState<
      Set<EditorCommand>
    >(new Set());

    // Determine if we're in controlled mode
    const isControlled = controlledValue !== undefined;
    const currentValue = isControlled ? controlledValue : internalValue;

    // Auto-focus on mount
    React.useEffect(() => {
      if (autoFocus && editorRef.current) {
        editorRef.current.focus();
      }
    }, [autoFocus]);

    // Update active commands based on cursor position
    const updateActiveCommands = React.useCallback(() => {
      const active = new Set<EditorCommand>();

      if (document.queryCommandState("bold")) active.add("bold");
      if (document.queryCommandState("italic")) active.add("italic");
      if (document.queryCommandState("underline")) active.add("underline");
      if (document.queryCommandState("strikeThrough"))
        active.add("strikethrough");
      if (document.queryCommandState("subscript")) active.add("subscript");
      if (document.queryCommandState("superscript")) active.add("superscript");
      if (document.queryCommandState("hiliteColor")) active.add("highlight");
      if (document.queryCommandState("insertUnorderedList")) active.add("ul");
      if (document.queryCommandState("insertOrderedList")) active.add("ol");
      if (document.queryCommandState("justifyLeft")) active.add("alignLeft");
      if (document.queryCommandState("justifyCenter"))
        active.add("alignCenter");
      if (document.queryCommandState("justifyRight")) active.add("alignRight");
      if (document.queryCommandState("justifyFull")) active.add("alignJustify");

      setActiveCommands(active);
    }, []);

    // Execute editor command
    const executeCommand = React.useCallback(
      (command: EditorCommand, _value?: string) => {
        if (disabled || readOnly) return;

        editorRef.current?.focus();

        try {
          switch (command) {
            case "bold":
              document.execCommand("bold", false);
              break;
            case "italic":
              document.execCommand("italic", false);
              break;
            case "underline":
              document.execCommand("underline", false);
              break;
            case "strikethrough":
              document.execCommand("strikeThrough", false);
              break;
            case "h1":
              document.execCommand("formatBlock", false, "<h1>");
              break;
            case "h2":
              document.execCommand("formatBlock", false, "<h2>");
              break;
            case "h3":
              document.execCommand("formatBlock", false, "<h3>");
              break;
            case "h4":
              document.execCommand("formatBlock", false, "<h4>");
              break;
            case "h5":
              document.execCommand("formatBlock", false, "<h5>");
              break;
            case "h6":
              document.execCommand("formatBlock", false, "<h6>");
              break;
            case "p":
              document.execCommand("formatBlock", false, "<p>");
              break;
            case "blockquote":
              document.execCommand("formatBlock", false, "<blockquote>");
              break;
            case "code":
              document.execCommand("formatBlock", false, "<pre>");
              break;
            case "ul":
              document.execCommand("insertUnorderedList", false);
              break;
            case "ol":
              document.execCommand("insertOrderedList", false);
              break;
            case "link": {
              const url = prompt("Enter URL:");
              if (url) {
                document.execCommand("createLink", false, url);
              }
              break;
            }
            case "image": {
              const imageUrl = prompt("Enter image URL:");
              if (imageUrl) {
                document.execCommand("insertImage", false, imageUrl);
              }
              break;
            }
            case "alignLeft":
              document.execCommand("justifyLeft", false);
              break;
            case "alignCenter":
              document.execCommand("justifyCenter", false);
              break;
            case "alignRight":
              document.execCommand("justifyRight", false);
              break;
            case "alignJustify":
              document.execCommand("justifyFull", false);
              break;
            case "indent":
              document.execCommand("indent", false);
              break;
            case "outdent":
              document.execCommand("outdent", false);
              break;
            case "undo":
              document.execCommand("undo", false);
              break;
            case "redo":
              document.execCommand("redo", false);
              break;
            case "clear":
              document.execCommand("removeFormat", false);
              break;
            case "hr":
              document.execCommand("insertHorizontalRule", false);
              break;
            case "subscript":
              document.execCommand("subscript", false);
              break;
            case "superscript":
              document.execCommand("superscript", false);
              break;
            case "highlight":
              document.execCommand("hiliteColor", false, "#ffff00");
              break;
            case "table": {
              const rows = prompt("Number of rows:", "3");
              const cols = prompt("Number of columns:", "3");
              if (rows && cols) {
                let tableHTML =
                  '<table border="1" style="border-collapse: collapse; width: 100%;">';
                for (let i = 0; i < parseInt(rows); i++) {
                  tableHTML += "<tr>";
                  for (let j = 0; j < parseInt(cols); j++) {
                    tableHTML +=
                      '<td style="border: 1px solid #ccc; padding: 8px;">&nbsp;</td>';
                  }
                  tableHTML += "</tr>";
                }
                tableHTML += "</table>";
                document.execCommand("insertHTML", false, tableHTML);
              }
              break;
            }
            case "codeblock":
              document.execCommand("formatBlock", false, "<pre>");
              break;
          }
        } catch (error) {
          // Suppress errors from execCommand (deprecated API)
          // This includes "cancelation" errors from browser extensions
          console.debug("TextEditor execCommand error (non-critical):", error);
        }

        updateActiveCommands();
        handleInput();
      },
      [disabled, readOnly, updateActiveCommands],
    );

    // Handle content changes
    const handleInput = React.useCallback(() => {
      if (editorRef.current) {
        try {
          const html = editorRef.current.innerHTML;
          const sanitizedHTML = sanitizeHTML(html);

          // If sanitization changed the content, update the editor
          if (sanitizedHTML !== html) {
            const cursorPosition = saveCursorPosition(editorRef.current);
            editorRef.current.innerHTML = sanitizedHTML;
            if (cursorPosition !== null) {
              restoreCursorPosition(editorRef.current, cursorPosition);
            }
          }

          if (!isControlled) {
            setInternalValue(sanitizedHTML);
          }
          // Call onChange without triggering re-render
          onChange?.(sanitizedHTML);
        } catch (error) {
          // Suppress any errors during input handling
          console.debug("TextEditor input error (non-critical):", error);
        }
      }
    }, [isControlled, onChange]);

    // Handle selection change
    React.useEffect(() => {
      const handleSelectionChange = () => {
        updateActiveCommands();
      };

      document.addEventListener("selectionchange", handleSelectionChange);
      return () => {
        document.removeEventListener("selectionchange", handleSelectionChange);
      };
    }, [updateActiveCommands]);

    // Set initial value with sanitization
    React.useEffect(() => {
      if (editorRef.current && editorRef.current.innerHTML === "") {
        editorRef.current.innerHTML = sanitizeHTML(defaultValue);
      }
    }, [defaultValue]);

    // Sync controlled value (only when external value changes) with sanitization
    React.useEffect(() => {
      if (
        isControlled &&
        editorRef.current &&
        controlledValue !== editorRef.current.innerHTML
      ) {
        // Sanitize the controlled value
        const sanitizedValue = sanitizeHTML(controlledValue);

        // Check if the editor is focused
        const isFocused = document.activeElement === editorRef.current;

        if (isFocused) {
          // Save cursor position before updating
          const cursorPosition = saveCursorPosition(editorRef.current);

          // Update content
          editorRef.current.innerHTML = sanitizedValue;

          // Restore cursor position after update
          if (cursorPosition !== null) {
            // Use requestAnimationFrame for better timing
            requestAnimationFrame(() => {
              if (editorRef.current) {
                restoreCursorPosition(editorRef.current, cursorPosition);
              }
            });
          }
        } else {
          // If not focused, just update the content
          editorRef.current.innerHTML = sanitizedValue;
        }
      }
    }, [isControlled, controlledValue]);

    return (
      <div
        ref={ref}
        className={cn(
          textEditorVariants({ variant, size, fullWidth }),
          className,
        )}
        {...props}
      >
        {/* Toolbar */}
        {showToolbar && !disabled && !readOnly && (
          <div
            className={cn(
              toolbarVariants({
                variant: toolbarVariant,
                size,
                sticky: stickyToolbar,
              }),
            )}
          >
            {toolbarButtons.map((button, index) => {
              if (button.separator) {
                return (
                  <div
                    key={`separator-${index}`}
                    className={toolbarSeparatorVariants()}
                  />
                );
              }

              if (!button.command) return null;

              const isActive = activeCommands.has(button.command);

              return (
                <button
                  key={button.command}
                  type="button"
                  onClick={() => executeCommand(button.command!)}
                  className={cn(
                    toolbarButtonVariants({ variant: toolbarVariant, size }),
                  )}
                  data-active={isActive}
                  title={button.tooltip || button.label}
                  disabled={disabled}
                  aria-label={button.tooltip || button.label}
                >
                  {button.icon}
                  {button.label && size !== "sm" && (
                    <span className="hidden xl:inline ml-1">
                      {button.label}
                    </span>
                  )}
                </button>
              );
            })}
          </div>
        )}

        {/* Editor Content */}
        <div
          ref={editorRef}
          contentEditable={!disabled && !readOnly}
          onInput={handleInput}
          onPaste={(e) => {
            if (!readOnly && !disabled) {
              e.preventDefault();
              // Get plain text and sanitize it
              const text = e.clipboardData.getData("text/plain");
              const sanitizedText = text.replace(
                /<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi,
                "",
              );
              document.execCommand("insertText", false, sanitizedText);
            }
          }}
          className={cn(editorContentVariants({ size }))}
          style={{
            minHeight: minHeight || "200px",
            maxHeight: maxHeight || "600px",
          }}
          data-placeholder={!currentValue ? placeholder : undefined}
          suppressContentEditableWarning
        />
      </div>
    );
  },
);

TextEditor.displayName = "TextEditor";

