export const ToastItem: React.FC<ToastItemProps> = ({
  id,
  title,
  description,
  status = "info",
  variant = "solid",
  animation = "slide",
  duration = 5000,
  closable = true,
  showIcon = true,
  icon,
  showProgress = true,
  action,
  onClose,
  onRemove,
  className,
  children,
  pauseOnHover = true,
  position = "top-right",
}) => {
  // Development-only validation
  useEffect(() => {
    const validator = createValidator("ToastItem");

    // Validate status
    validator.validateEnum("status", status, [
      "info",
      "success",
      "warning",
      "danger",
    ] as const);

    // Validate variant
    validator.validateEnum("variant", variant, [
      "solid",
      "subtle",
      "outline",
      "glass",
    ] as const);

    // Validate animation
    validator.validateEnum("animation", animation, [
      "slide",
      "fade",
      "scale",
      "bounce",
    ] as const);

    // Validate position
    validator.validateEnum("position", position, [
      "top-left",
      "top-center",
      "top-right",
      "bottom-left",
      "bottom-center",
      "bottom-right",
    ] as const);

    // Validate duration
    validator.validateType("duration", duration, "number", isValidNumber);
    if (duration !== undefined && duration < 0) {
      validator.error("duration must be a positive number");
    }

    // Validate boolean props
    validator.validateType("closable", closable, "boolean", isValidBoolean);
    validator.validateType("showIcon", showIcon, "boolean", isValidBoolean);
    validator.validateType(
      "showProgress",
      showProgress,
      "boolean",
      isValidBoolean,
    );
    validator.validateType(
      "pauseOnHover",
      pauseOnHover,
      "boolean",
      isValidBoolean,
    );

    // Validate content
    if (!title && !description && !children) {
      validator.warn(
        "ToastItem should have title, description, or children for accessibility",
      );
    }

    // Common validators
    commonValidators.className(validator, className);
  }, [
    status,
    variant,
    animation,
    position,
    duration,
    closable,
    showIcon,
    showProgress,
    pauseOnHover,
    title,
    description,
    children,
    className,
  ]);

  const [isVisible, setIsVisible] = useState(false);
  const [isExiting, setIsExiting] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [progress, setProgress] = useState(100);
  const timerRef = useRef<NodeJS.Timeout | null>(null);
  const startTimeRef = useRef<number>(Date.now());
  const remainingTimeRef = useRef<number>(duration);

  // Animation classes based on position and animation type
  const getAnimationClasses = () => {
    if (!isVisible && !isExiting) {
      // Initial state (entering)
      switch (animation) {
        case "slide":
          if (position.includes("right")) return "translate-x-full opacity-0";
          if (position.includes("left")) return "-translate-x-full opacity-0";
          if (position.includes("top")) return "-translate-y-full opacity-0";
          return "translate-y-full opacity-0";
        case "fade":
          return "opacity-0";
        case "scale":
          return "scale-95 opacity-0";
        case "bounce":
          return "scale-50 opacity-0";
        default:
          return "translate-x-full opacity-0";
      }
    }

    if (isExiting) {
      // Exiting state
      switch (animation) {
        case "slide":
          if (position.includes("right")) return "translate-x-full opacity-0";
          if (position.includes("left")) return "-translate-x-full opacity-0";
          if (position.includes("top")) return "-translate-y-full opacity-0";
          return "translate-y-full opacity-0";
        case "fade":
          return "opacity-0";
        case "scale":
          return "scale-95 opacity-0";
        case "bounce":
          return "scale-50 opacity-0";
        default:
          return "translate-x-full opacity-0";
      }
    }

    // Visible state
    return "translate-x-0 translate-y-0 scale-100 opacity-100";
  };

  const startTimer = () => {
    if (duration === 0) return;

    startTimeRef.current = Date.now();
    timerRef.current = setTimeout(() => {
      handleClose();
    }, remainingTimeRef.current);

    // Update progress
    let isCancelled = false;
    const interval = setInterval(() => {
      if (isCancelled) return;
      if (!isPaused) {
        const elapsed = Date.now() - startTimeRef.current;
        const newProgress = Math.max(0, 100 - (elapsed / duration) * 100);
        setProgress(newProgress);
        if (newProgress === 0) {
          try {
            clearInterval(interval);
          } catch (err) {
            console.debug("Toast interval clear error suppressed:", err);
          }
        }
      }
    }, 16); // ~60fps

    return () => {
      isCancelled = true;
      try {
        clearInterval(interval);
      } catch (err) {
        console.debug("Toast interval cleanup error suppressed:", err);
      }
    };
  };

  const pauseTimer = () => {
    if (timerRef.current) {
      try {
        clearTimeout(timerRef.current);
        const elapsed = Date.now() - startTimeRef.current;
        remainingTimeRef.current -= elapsed;
      } catch (err) {
        console.debug("Toast pause timer error suppressed:", err);
      }
    }
  };

  const resumeTimer = () => {
    startTimer();
  };

  const handleClose = () => {
    setIsExiting(true);
    let isCancelled = false;
    const closeTimer = setTimeout(() => {
      if (!isCancelled) {
        onClose?.();
        onRemove(id);
      }
    }, 300);

    // Store for cleanup
    return () => {
      isCancelled = true;
      try {
        clearTimeout(closeTimer);
      } catch (err) {
        console.debug("Toast close timer error suppressed:", err);
      }
    };
  };

  useEffect(() => {
    let isCancelled = false;
    // Enter animation
    const enterTimer = setTimeout(() => {
      if (!isCancelled) setIsVisible(true);
    }, 50);

    // Auto dismiss timer
    const cleanup = startTimer();

    return () => {
      isCancelled = true;
      try {
        clearTimeout(enterTimer);
      } catch (err) {
        console.debug("Toast enter timer error suppressed:", err);
      }
      if (timerRef.current) {
        try {
          clearTimeout(timerRef.current);
        } catch (err) {
          console.debug("Toast timer cleanup error suppressed:", err);
        }
      }
      cleanup?.();
    };
  }, []);

  useEffect(() => {
    if (isPaused) {
      pauseTimer();
    } else if (isVisible && !isExiting) {
      resumeTimer();
    }
  }, [isPaused]);

  const handleMouseEnter = () => {
    if (pauseOnHover) setIsPaused(true);
  };

  const handleMouseLeave = () => {
    if (pauseOnHover) setIsPaused(false);
  };

  return (
    <div
      className={cn(
        toastVariants({ variant, status, animation }),
        "rounded-xl",
        getAnimationClasses(),
        className,
      )}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      role="alert"
      aria-live="polite"
    >
      {/* Thin, clean progress bar */}
      {showProgress && duration > 0 && (
        <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-current/15 rounded-b-xl overflow-hidden">
          <div
            className="h-full bg-current/80 transition-all duration-75 ease-linear"
            style={{ width: `${progress}%` }}
          />
        </div>
      )}

      {/* Clean icon with simple background */}
      {(showIcon || icon) && (
        <div className="flex-shrink-0">{icon || StatusIcons[status]}</div>
      )}

      {/* Clean content */}
      <div className="flex-1 min-w-0">
        {children ? (
          children
        ) : (
          <>
            {title && (
              <div className="font-semibold text-sm leading-tight">{title}</div>
            )}
            {description && (
              <div className="text-xs opacity-90 leading-snug mt-0.5">
                {description}
              </div>
            )}
          </>
        )}

        {/* Simple action button */}
        {action && (
          <button
            onClick={action.onClick}
            className="mt-2 text-xs font-medium px-2 py-1 rounded-md bg-current/10 hover:bg-current/20 transition-colors"
          >
            {action.label}
          </button>
        )}
      </div>

      {/* Simple close button */}
      {closable && (
        <button
          onClick={handleClose}
          className="flex-shrink-0 p-1 rounded-md hover:bg-current/10 transition-colors"
          aria-label="Close notification"
        >
          <svg
            className="w-4 h-4"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            strokeWidth={2}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        </button>
      )}
    </div>
  );
};

"use client";
import React, { createContext, useContext, useState, useCallback } from "react";
import { createPortal } from "react-dom";
import type {
  ToastProviderProps,
  ToastContextValue,
  ToastProps,
  ToastPosition,
} from "./Toast.types";
import { ToastItem } from "./ToastItem";
import { cn } from "../../lib/utils";

const ToastContext = createContext<ToastContextValue | undefined>(undefined);

export const useToast = (): ToastContextValue => {
  const context = useContext(ToastContext);
  if (!context) {
    throw new Error("useToast must be used within ToastProvider");
  }
  return context;
};

interface ToastWithId extends ToastProps {
  id: string;
}

// Position classes for toast containers
const positionClasses: Record<ToastPosition, string> = {
  "top-left": "top-0 left-0 items-start",
  "top-center": "top-0 left-1/2 -translate-x-1/2 items-center",
  "top-right": "top-0 right-0 items-end",
  "bottom-left": "bottom-0 left-0 items-start",
  "bottom-center": "bottom-0 left-1/2 -translate-x-1/2 items-center",
  "bottom-right": "bottom-0 right-0 items-end",
};

export const ToastProvider: React.FC<ToastProviderProps> = ({
  children,
  position = "top-right",
  duration = 5000,
  max = 5,
  gap = 12,
  offset = 16,
  variant = "solid",
  animation = "slide",
}) => {
  const [toasts, setToasts] = useState<ToastWithId[]>([]);

  const generateId = () =>
    `toast-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

  const toast = useCallback(
    (options: Omit<ToastProps, "id">): string => {
      const id = generateId();
      const newToast: ToastWithId = {
        id,
        position: options.position || position,
        duration: options.duration ?? duration,
        variant: options.variant || variant,
        animation: options.animation || animation,
        ...options,
      };

      setToasts((prev) => {
        // Limit max toasts
        const updated = [...prev, newToast];
        if (updated.length > max) {
          return updated.slice(-max);
        }
        return updated;
      });

      return id;
    },
    [position, duration, max, variant, animation]
  );

  const close = useCallback((id: string) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  }, []);

  const closeAll = useCallback(() => {
    setToasts([]);
  }, []);

  const update = useCallback((id: string, options: Partial<ToastProps>) => {
    setToasts((prev) =>
      prev.map((t) => (t.id === id ? { ...t, ...options } : t))
    );
  }, []);

  // Shorthand methods with flexible parameters
  // 1 param: description
  // 2 params: description, title
  // 3 params: description, title, options
  const info = useCallback(
    (
      description: string,
      titleOrOptions?: string | Partial<ToastProps>,
      options?: Partial<ToastProps>
    ) => {
      if (typeof titleOrOptions === "string") {
        return toast({
          description,
          title: titleOrOptions,
          status: "info",
          ...options,
        });
      }
      return toast({ description, status: "info", ...titleOrOptions });
    },
    [toast]
  );

  const success = useCallback(
    (
      description: string,
      titleOrOptions?: string | Partial<ToastProps>,
      options?: Partial<ToastProps>
    ) => {
      if (typeof titleOrOptions === "string") {
        return toast({
          description,
          title: titleOrOptions,
          status: "success",
          ...options,
        });
      }
      return toast({ description, status: "success", ...titleOrOptions });
    },
    [toast]
  );

  const warning = useCallback(
    (
      description: string,
      titleOrOptions?: string | Partial<ToastProps>,
      options?: Partial<ToastProps>
    ) => {
      if (typeof titleOrOptions === "string") {
        return toast({
          description,
          title: titleOrOptions,
          status: "warning",
          ...options,
        });
      }
      return toast({ description, status: "warning", ...titleOrOptions });
    },
    [toast]
  );

  const error = useCallback(
    (
      description: string,
      titleOrOptions?: string | Partial<ToastProps>,
      options?: Partial<ToastProps>
    ) => {
      if (typeof titleOrOptions === "string") {
        return toast({
          description,
          title: titleOrOptions,
          status: "danger",
          ...options,
        });
      }
      return toast({ description, status: "danger", ...titleOrOptions });
    },
    [toast]
  );

  const contextValue: ToastContextValue = {
    toast,
    info,
    success,
    warning,
    error,
    close,
    closeAll,
    update,
  };

  // Group toasts by position
  const toastsByPosition = toasts.reduce((acc, toast) => {
    const pos = toast.position || position;
    if (!acc[pos]) acc[pos] = [];
    acc[pos].push(toast);
    return acc;
  }, {} as Record<ToastPosition, ToastWithId[]>);

  return (
    <ToastContext.Provider value={contextValue}>
      {children}
      {typeof document !== "undefined" &&
        createPortal(
          <>
            {Object.entries(toastsByPosition).map(([pos, posToasts]) => (
              <div
                key={pos}
                className={cn(
                  "fixed z-[9999] flex flex-col pointer-events-none",
                  positionClasses[pos as ToastPosition]
                )}
                style={{
                  padding: `${offset}px`,
                  gap: `${gap}px`,
                }}
              >
                {posToasts.map((toast, index) => (
                  <ToastItem
                    key={toast.id}
                    {...toast}
                    onRemove={close}
                    index={index}
                  />
                ))}
              </div>
            ))}
          </>,
          document.body
        )}
    </ToastContext.Provider>
  );
};

