import * as React from "react";
import { cn } from "../../lib/utils";
import {
  toggleGroupVariants,
  toggleGroupItemVariants,
} from "./ToggleGroup.styles";
import type {
  ToggleGroupProps,
  ToggleGroupItemProps,
  ToggleGroupContextValue,
} from "./ToggleGroup.types";

// ============================================================================
// CONTEXT
// ============================================================================

const ToggleGroupContext = React.createContext<
  ToggleGroupContextValue | undefined
>(undefined);

function useToggleGroupContext() {
  const context = React.useContext(ToggleGroupContext);
  if (!context) {
    throw new Error(
      "ToggleGroupItem must be used within a ToggleGroup component"
    );
  }
  return context;
}

// ============================================================================
// TOGGLE GROUP COMPONENT
// ============================================================================

/**
 * ToggleGroup Component
 *
 * A group of toggle buttons that can be used for single or multiple selection.
 * Supports both controlled and uncontrolled state, with custom spacing and orientation.
 *
 * @example
 * ```tsx
 * // Single selection (radio behavior)
 * <ToggleGroup type="single" value={value} onValueChange={setValue}>
 *   <ToggleGroupItem value="left">Left</ToggleGroupItem>
 *   <ToggleGroupItem value="center">Center</ToggleGroupItem>
 *   <ToggleGroupItem value="right">Right</ToggleGroupItem>
 * </ToggleGroup>
 *
 * // Multiple selection (checkbox behavior)
 * <ToggleGroup type="multiple" value={values} onValueChange={setValues}>
 *   <ToggleGroupItem value="bold">B</ToggleGroupItem>
 *   <ToggleGroupItem value="italic">I</ToggleGroupItem>
 *   <ToggleGroupItem value="underline">U</ToggleGroupItem>
 * </ToggleGroup>
 *
 * // With spacing and orientation
 * <ToggleGroup
 *   type="single"
 *   variant="primary"
 *   spacing={0}
 *   orientation="vertical"
 * >
 *   <ToggleGroupItem value="top">Top</ToggleGroupItem>
 *   <ToggleGroupItem value="bottom">Bottom</ToggleGroupItem>
 * </ToggleGroup>
 * ```
 */
export const ToggleGroup = React.forwardRef<HTMLDivElement, ToggleGroupProps>(
  (
    {
      className,
      variant = "default",
      size = "default",
      spacing = 1,
      orientation = "horizontal",
      type = "single",
      value: controlledValue,
      defaultValue,
      onValueChange,
      disabled = false,
      children,
      ...props
    },
    ref
  ) => {
    // Uncontrolled state
    const [uncontrolledValue, setUncontrolledValue] = React.useState<
      string | string[] | undefined
    >(() => {
      if (defaultValue !== undefined) {
        return defaultValue;
      }
      return type === "multiple" ? [] : undefined;
    });

    // Determine current value
    const value =
      controlledValue !== undefined ? controlledValue : uncontrolledValue;

    // Handle value change
    const handleValueChange = React.useCallback(
      (newValue: string | string[]) => {
        if (controlledValue === undefined) {
          setUncontrolledValue(newValue);
        }
        onValueChange?.(newValue);
      },
      [controlledValue, onValueChange]
    );

    // Context value
    const contextValue: ToggleGroupContextValue = React.useMemo(
      () => ({
        type,
        value,
        onValueChange: handleValueChange,
        variant,
        size,
        spacing,
        orientation,
        disabled,
      }),
      [
        type,
        value,
        handleValueChange,
        variant,
        size,
        spacing,
        orientation,
        disabled,
      ]
    );

    return (
      <div
        ref={ref}
        role={type === "single" ? "radiogroup" : "group"}
        aria-orientation={orientation}
        className={cn(
          toggleGroupVariants({ variant, orientation, spacing }),
          className
        )}
        data-variant={variant}
        data-size={size}
        data-spacing={spacing}
        data-orientation={orientation}
        {...props}
      >
        <ToggleGroupContext.Provider value={contextValue}>
          {children}
        </ToggleGroupContext.Provider>
      </div>
    );
  }
);

ToggleGroup.displayName = "ToggleGroup";

// ============================================================================
// TOGGLE GROUP ITEM COMPONENT
// ============================================================================

/**
 * ToggleGroupItem Component
 *
 * Individual toggle button within a ToggleGroup.
 * Must be used as a child of ToggleGroup.
 *
 * @example
 * ```tsx
 * <ToggleGroupItem value="option1">
 *   Option 1
 * </ToggleGroupItem>
 *
 * // With icon
 * <ToggleGroupItem value="bold">
 *   <Bold className="h-4 w-4" />
 * </ToggleGroupItem>
 *
 * // Disabled item
 * <ToggleGroupItem value="disabled" disabled>
 *   Disabled
 * </ToggleGroupItem>
 * ```
 */
export const ToggleGroupItem = React.forwardRef<
  HTMLButtonElement,
  ToggleGroupItemProps
>(
  (
    {
      className,
      value,
      disabled: itemDisabled = false,
      children,
      onClick,
      ...props
    },
    ref
  ) => {
    const context = useToggleGroupContext();
    const {
      type,
      value: groupValue,
      onValueChange,
      variant,
      size,
      spacing,
      orientation,
      disabled: groupDisabled,
    } = context;

    const disabled = groupDisabled || itemDisabled;

    // Determine if this item is selected
    const isSelected = React.useMemo(() => {
      if (groupValue === undefined) return false;
      if (type === "single") {
        return groupValue === value;
      } else {
        return Array.isArray(groupValue) && groupValue.includes(value);
      }
    }, [type, groupValue, value]);

    // Handle click
    const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
      if (disabled) return;

      if (type === "single") {
        // For single selection, allow deselection by clicking the same item
        const newValue = isSelected ? undefined : value;
        onValueChange(newValue as string);
      } else {
        // For multiple selection
        const currentValues = Array.isArray(groupValue) ? groupValue : [];
        const newValues = isSelected
          ? currentValues.filter((v) => v !== value)
          : [...currentValues, value];
        onValueChange(newValues);
      }

      onClick?.(event);
    };

    return (
      <button
        ref={ref}
        type="button"
        role={type === "single" ? "radio" : "checkbox"}
        aria-checked={isSelected}
        aria-pressed={isSelected}
        data-state={isSelected ? "on" : "off"}
        data-variant={variant}
        data-size={size}
        data-spacing={spacing}
        data-orientation={orientation}
        disabled={disabled}
        onClick={handleClick}
        className={cn(
          toggleGroupItemVariants({ variant, size, spacing, orientation }),
          className
        )}
        {...props}
      >
        {children}
      </button>
    );
  }
);

ToggleGroupItem.displayName = "ToggleGroupItem";

// ============================================================================
// EXPORTS
// ============================================================================

export type {
  ToggleGroupProps,
  ToggleGroupItemProps,
  ToggleGroupVariant,
  ToggleGroupSize,
  ToggleGroupSpacing,
  ToggleGroupOrientation,
} from "./ToggleGroup.types";

export { toggleGroupVariants, toggleGroupItemVariants };

"use client";
import * as React from "react";
import { cn } from "../../lib/utils";
import { toggleVariants, toggleGroupVariants } from "./Toggle.styles";
import type {
  ToggleProps,
  ToggleGroupProps,
  ToggleVariant,
  ToggleSize,
} from "./Toggle.types";
import { Slot } from "../../lib/Slot";

// ============================================================================
// CONTEXT
// ============================================================================

interface ToggleGroupContextValue {
  type: "single" | "multiple";
  value: string | string[] | undefined;
  onValueChange: (value: string | string[]) => void;
  variant?: ToggleVariant;
  size?: ToggleSize;
  disabled?: boolean;
}

const ToggleGroupContext = React.createContext<
  ToggleGroupContextValue | undefined
>(undefined);

function useToggleGroup() {
  return React.useContext(ToggleGroupContext);
}

// ============================================================================
// TOGGLE COMPONENT
// ============================================================================

/**
 * Toggle Component
 *
 * A button that can be toggled between on and off states.
 * Features multiple variants, sizes, and support for icons and badges.
 *
 * @example
 * ```tsx
 * // Compact API
 * <Toggle pressed={bold} onPressedChange={setBold}>
 *   <Bold className="h-4 w-4" />
 * </Toggle>
 *
 * // With variants
 * <Toggle variant="primary" size="lg">
 *   Toggle me
 * </Toggle>
 * ```
 */
export const Toggle = React.forwardRef<HTMLButtonElement, ToggleProps>(
  (
    {
      className,
      variant = "default",
      size = "default",
      pressed: controlledPressed,
      defaultPressed = false,
      onPressedChange,
      disabled = false,
      children,
      value,
      asChild = false,
      ...props
    },
    ref,
  ) => {
    const groupContext = useToggleGroup();
    const [uncontrolledPressed, setUncontrolledPressed] =
      React.useState(defaultPressed);

    // Determine if this toggle is part of a group
    const inGroup = groupContext !== undefined;

    // Determine pressed state
    let isPressed: boolean;
    if (inGroup && value !== undefined) {
      if (groupContext.type === "single") {
        isPressed = groupContext.value === value;
      } else {
        isPressed = Array.isArray(groupContext.value)
          ? groupContext.value.includes(value)
          : false;
      }
    } else {
      isPressed =
        controlledPressed !== undefined
          ? controlledPressed
          : uncontrolledPressed;
    }

    // Handle toggle click
    const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
      if (disabled || (inGroup && groupContext.disabled)) {
        return;
      }

      if (inGroup && value !== undefined) {
        // Group mode
        if (groupContext.type === "single") {
          groupContext.onValueChange(isPressed ? "" : value);
        } else {
          const currentValues = Array.isArray(groupContext.value)
            ? groupContext.value
            : [];
          const newValues = isPressed
            ? currentValues.filter((v) => v !== value)
            : [...currentValues, value];
          groupContext.onValueChange(newValues);
        }
      } else {
        // Standalone mode
        const newPressed = !isPressed;
        if (controlledPressed === undefined) {
          setUncontrolledPressed(newPressed);
        }
        onPressedChange?.(newPressed);
      }

      props.onClick?.(event);
    };

    const effectiveVariant = inGroup
      ? groupContext.variant || variant
      : variant;
    const effectiveSize = inGroup ? groupContext.size || size : size;
    const effectiveDisabled = disabled || (inGroup && groupContext.disabled);

    const Comp = asChild ? Slot : "button";

    return (
      <Comp
        ref={ref}
        type="button"
        role="button"
        aria-pressed={isPressed}
        data-state={isPressed ? "on" : "off"}
        data-slot="toggle"
        disabled={effectiveDisabled}
        className={cn(
          toggleVariants({
            variant: effectiveVariant,
            size: effectiveSize,
          }),
          className,
        )}
        onClick={handleClick}
        {...props}
      >
        {children}
      </Comp>
    );
  },
);

Toggle.displayName = "Toggle";

// ============================================================================
// TOGGLE GROUP COMPONENT
// ============================================================================

/**
 * ToggleGroup Component
 *
 * Groups multiple Toggle components together with shared state management.
 * Supports single selection (like radio) or multiple selection (like checkbox).
 *
 * @example
 * ```tsx
 * // Single selection
 * <ToggleGroup type="single" value={align} onValueChange={setAlign}>
 *   <Toggle value="left"><AlignLeft /></Toggle>
 *   <Toggle value="center"><AlignCenter /></Toggle>
 *   <Toggle value="right"><AlignRight /></Toggle>
 * </ToggleGroup>
 *
 * // Multiple selection
 * <ToggleGroup type="multiple" value={styles} onValueChange={setStyles}>
 *   <Toggle value="bold"><Bold /></Toggle>
 *   <Toggle value="italic"><Italic /></Toggle>
 *   <Toggle value="underline"><Underline /></Toggle>
 * </ToggleGroup>
 * ```
 */
export const ToggleGroup = React.forwardRef<HTMLDivElement, ToggleGroupProps>(
  (
    {
      className,
      type = "single",
      value,
      defaultValue,
      onValueChange,
      variant,
      size,
      disabled = false,
      orientation = "horizontal",
      children,
      ...props
    },
    ref,
  ) => {
    const [uncontrolledValue, setUncontrolledValue] = React.useState<
      string | string[]
    >(defaultValue || (type === "single" ? "" : ([] as string[])));

    const effectiveValue = value !== undefined ? value : uncontrolledValue;

    const handleValueChange = React.useCallback(
      (newValue: string | string[]) => {
        if (value === undefined) {
          setUncontrolledValue(newValue);
        }
        if (onValueChange) {
          onValueChange(newValue as any);
        }
      },
      [value, onValueChange],
    );

    const contextValue: ToggleGroupContextValue = React.useMemo(
      () => ({
        type,
        value: effectiveValue,
        onValueChange: handleValueChange,
        variant,
        size,
        disabled,
      }),
      [type, effectiveValue, handleValueChange, variant, size, disabled],
    );

    return (
      <ToggleGroupContext.Provider value={contextValue}>
        <div
          ref={ref}
          role="group"
          data-slot="toggle-group"
          className={cn(toggleGroupVariants({ orientation }), className)}
          {...props}
        >
          {children}
        </div>
      </ToggleGroupContext.Provider>
    );
  },
);

ToggleGroup.displayName = "ToggleGroup";

// ============================================================================
// EXPORTS
// ============================================================================

export type { ToggleProps, ToggleGroupProps, ToggleVariant, ToggleSize };
export { toggleVariants, toggleGroupVariants };

"use client";
import React, {
  useState,
  useEffect,
  useRef,
  createContext,
  useContext,
} from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import {
  TooltipProps,
  TooltipContentProps,
  TooltipTriggerProps,
  TooltipContextValue,
} from "./Tooltip.types";
import {
  createValidator,
  commonValidators,
  isValidBoolean,
  isValidNumber,
} from "../../lib/validation";
import { tooltipVariants, arrowVariants } from "./Tooltip.styles";

export type TooltipVariantsProps = VariantProps<typeof tooltipVariants>;

// Create Tooltip Context
const TooltipContext = createContext<TooltipContextValue | undefined>(
  undefined
);

const useTooltip = () => {
  const context = useContext(TooltipContext);
  if (!context) {
    throw new Error("Tooltip components must be used within a Tooltip");
  }
  return context;
};

/**
 * Tooltip Component
 *
 * A composable tooltip component for displaying contextual information,
 * hints, and descriptions with multiple visual styles and positioning options.
 *
 * @example
 * ```tsx
 * // Basic tooltip
 * <Tooltip>
 *   <TooltipTrigger>
 *     <Button>Hover me</Button>
 *   </TooltipTrigger>
 *   <TooltipContent>This is a tooltip</TooltipContent>
 * </Tooltip>
 *
 * // Variant with position
 * <Tooltip variant="success" position="right">
 *   <TooltipTrigger>
 *     <span>âœ“</span>
 *   </TooltipTrigger>
 *   <TooltipContent>Success message</TooltipContent>
 * </Tooltip>
 *
 * // Interactive tooltip
 * <Tooltip interactive trigger="click">
 *   <TooltipTrigger>
 *     <Button>Click for info</Button>
 *   </TooltipTrigger>
 *   <TooltipContent>
 *     <div>Click <a href="#">here</a></div>
 *   </TooltipContent>
 * </Tooltip>
 * ```
 */
const Tooltip = React.forwardRef<HTMLDivElement, TooltipProps>(
  (
    {
      children,
      position = "top",
      variant = "default",
      size = "md",
      delay = 200,
      arrow = true,
      trigger = "hover",
      open: controlledOpen,
      onOpenChange,
      className,
      maxWidth = "320px",
      interactive = false,
      offset = 8,
      disabled = false,
      ...props
    },
    _ref
  ) => {
    const [internalOpen, setInternalOpen] = useState(false);
    const [timeoutId, setTimeoutId] = useState<NodeJS.Timeout | null>(null);
    const wrapperRef = useRef<HTMLDivElement>(null);

    // Use controlled or uncontrolled state
    const isOpen = controlledOpen !== undefined ? controlledOpen : internalOpen;

    // Development-only validation
    useEffect(() => {
      const validator = createValidator("Tooltip");

      // Validate variant
      validator.validateEnum("variant", variant, [
        "default",
        "dark",
        "light",
        "glass",
        "primary",
        "success",
        "warning",
        "error",
        "info",
      ] as const);

      // Validate size
      validator.validateEnum("size", size, ["sm", "md", "lg"] as const);

      // Validate position
      validator.validateEnum("position", position, [
        "top",
        "bottom",
        "left",
        "right",
      ] as const);

      // Validate trigger
      validator.validateEnum("trigger", trigger, [
        "hover",
        "click",
        "focus",
      ] as const);

      // Validate boolean props
      validator.validateType("arrow", arrow, "boolean", isValidBoolean);
      validator.validateType(
        "interactive",
        interactive,
        "boolean",
        isValidBoolean
      );
      validator.validateType("disabled", disabled, "boolean", isValidBoolean);

      // Validate numeric props
      validator.validateType("delay", delay, "number", isValidNumber);
      validator.validateType("offset", offset, "number", isValidNumber);

      // Validate children
      if (!children) {
        validator.warn("Tooltip should have children (trigger element)");
      }

      // Common validators
      commonValidators.className(validator, className);
      commonValidators.disabled(validator, disabled);
    }, [
      variant,
      size,
      position,
      trigger,
      arrow,
      interactive,
      disabled,
      delay,
      offset,
      children,
      className,
    ]);

    const setOpen = (open: boolean) => {
      if (disabled) return;

      if (controlledOpen === undefined) {
        setInternalOpen(open);
      }
      onOpenChange?.(open);
    };

    const handleMouseEnter = () => {
      if (trigger !== "hover" || disabled) return;
      if (timeoutId) clearTimeout(timeoutId);
      const id = setTimeout(() => setOpen(true), delay);
      setTimeoutId(id);
    };

    const handleMouseLeave = () => {
      if (trigger !== "hover" || disabled) return;
      if (timeoutId) clearTimeout(timeoutId);
      if (!interactive) {
        setOpen(false);
      }
    };

    const handleClick = () => {
      if (trigger !== "click" || disabled) return;
      setOpen(!isOpen);
    };

    const handleFocus = () => {
      if (trigger !== "focus" || disabled) return;
      setOpen(true);
    };

    const handleBlur = () => {
      if (trigger !== "focus" || disabled) return;
      setOpen(false);
    };

    // Close on outside click for interactive tooltips
    useEffect(() => {
      if (!interactive || !isOpen || trigger !== "click") return;

      const handleClickOutside = (event: MouseEvent) => {
        if (
          wrapperRef.current &&
          !wrapperRef.current.contains(event.target as Node)
        ) {
          setOpen(false);
        }
      };

      document.addEventListener("mousedown", handleClickOutside);
      return () =>
        document.removeEventListener("mousedown", handleClickOutside);
    }, [interactive, isOpen, trigger]);

    // Clean up timeout on unmount
    useEffect(() => {
      return () => {
        if (timeoutId) clearTimeout(timeoutId);
      };
    }, [timeoutId]);

    const contextValue: TooltipContextValue = {
      isOpen,
      setOpen,
      position,
      variant,
      size,
      arrow,
      interactive,
      maxWidth,
      offset,
      trigger,
      delay,
      disabled,
    };

    return (
      <TooltipContext.Provider value={contextValue}>
        <div
          ref={wrapperRef}
          className={cn("relative inline-flex group", className)}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          onClick={handleClick}
          onFocus={handleFocus}
          onBlur={handleBlur}
          {...props}
        >
          {children}
        </div>
      </TooltipContext.Provider>
    );
  }
);

Tooltip.displayName = "Tooltip";

/**
 * TooltipTrigger Component
 *
 * The element that triggers the tooltip display
 */
export const TooltipTrigger = React.forwardRef<
  HTMLDivElement,
  TooltipTriggerProps
>(({ children, className, asChild = false, ...props }, ref) => {
  const Comp = asChild ? React.Fragment : "div";
  const childProps = asChild ? {} : { ref, className, ...props };

  return <Comp {...childProps}>{children}</Comp>;
});

TooltipTrigger.displayName = "TooltipTrigger";

/**
 * TooltipContent Component
 *
 * The content displayed in the tooltip
 */
export const TooltipContent = React.forwardRef<
  HTMLDivElement,
  TooltipContentProps
>(({ children, className, ...props }, ref) => {
  const {
    isOpen,
    position,
    variant,
    size,
    arrow,
    interactive,
    maxWidth,
    offset,
    disabled,
  } = useTooltip();

  // Calculate offset based on position
  const getOffsetStyle = () => {
    switch (position) {
      case "top":
        return { marginBottom: `${offset}px` };
      case "bottom":
        return { marginTop: `${offset}px` };
      case "left":
        return { marginRight: `${offset}px` };
      case "right":
        return { marginLeft: `${offset}px` };
    }
  };

  if (disabled) return null;

  return (
    <div
      ref={ref}
      className={cn(
        tooltipVariants({ variant, size, position, interactive }),
        isOpen
          ? "opacity-100 scale-100 visible"
          : "opacity-0 scale-95 invisible",
        className
      )}
      style={{
        maxWidth,
        ...getOffsetStyle(),
      }}
      role="tooltip"
      aria-hidden={!isOpen}
      {...props}
    >
      {/* Content */}
      <div className="relative z-10">{children}</div>

      {/* Arrow */}
      {arrow && <div className={cn(arrowVariants({ variant, position }))} />}

      {/* Subtle glow effect */}
      <div className="absolute inset-0 rounded-inherit bg-gradient-to-br from-white/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none" />
    </div>
  );
});

TooltipContent.displayName = "TooltipContent";

export default Tooltip;

"use client";

import React, { useState, useMemo } from "react";
import { type VariantProps } from "class-variance-authority";
import { cn } from "../../lib/utils";
import { ChevronRight, ChevronLeft, Search, Inbox } from "lucide-react";
import type {
  TransferProps,
  TransferItem,
  TransferListProps,
} from "./Transfer.types";
import {
  transferVariants,
  transferListVariants,
  transferListHeaderVariants,
  transferListBodyVariants,
  transferItemVariants,
  transferOperationsVariants,
  transferOperationButtonVariants,
  transferSearchVariants,
  transferCheckboxVariants,
  transferEmptyVariants,
  transferFooterVariants,
} from "./Transfer.styles";

export type TransferVariantsProps = VariantProps<typeof transferVariants>;

/**
 * TransferList Component (internal)
 * Represents a single list in the transfer component
 */
const TransferList: React.FC<TransferListProps> = ({
  direction: _direction,
  title,
  items,
  selectedKeys,
  onSelectChange,
  onItemClick,
  disabled = false,
  showSearch = false,
  onSearch,
  searchPlaceholder = "Search...",
  showSelectAll = true,
  showItemCount = true,
  render,
  footer,
  emptyText,
  listClassName,
  itemClassName,
  height,
  filterOption,
  size = "md",
}) => {
  const [searchValue, setSearchValue] = useState("");

  // Filter items based on search
  const filteredItems = useMemo(() => {
    if (!searchValue) return items;

    if (filterOption) {
      return items.filter((item) => filterOption(searchValue, item));
    }

    return items.filter((item) =>
      item.label.toLowerCase().includes(searchValue.toLowerCase()),
    );
  }, [items, searchValue, filterOption]);

  // Check if all items are selected
  const allChecked = useMemo(() => {
    const selectableItems = filteredItems.filter((item) => !item.disabled);
    return (
      selectableItems.length > 0 &&
      selectableItems.every((item) => selectedKeys.includes(item.key))
    );
  }, [filteredItems, selectedKeys]);

  // Check if some items are selected (indeterminate state)
  const someChecked = useMemo(() => {
    return (
      !allChecked &&
      filteredItems.some((item) => selectedKeys.includes(item.key))
    );
  }, [filteredItems, selectedKeys, allChecked]);

  // Handle select all
  const handleSelectAll = () => {
    const selectableItems = filteredItems.filter((item) => !item.disabled);

    if (allChecked) {
      // Deselect all
      const newSelectedKeys = selectedKeys.filter(
        (key) => !selectableItems.find((item) => item.key === key),
      );
      onSelectChange(newSelectedKeys);
    } else {
      // Select all
      const newKeys = selectableItems.map((item) => item.key);
      const newSelectedKeys = [
        ...selectedKeys.filter(
          (key) => !selectableItems.find((item) => item.key === key),
        ),
        ...newKeys,
      ];
      onSelectChange(newSelectedKeys);
    }
  };

  // Handle item selection
  const handleItemClick = (item: TransferItem) => {
    if (item.disabled || disabled) return;

    const isSelected = selectedKeys.includes(item.key);

    if (isSelected) {
      onSelectChange(selectedKeys.filter((key) => key !== item.key));
    } else {
      onSelectChange([...selectedKeys, item.key]);
    }

    onItemClick?.(item);
  };

  // Handle search
  const handleSearch = (value: string) => {
    setSearchValue(value);
    onSearch?.(value);
  };

  return (
    <div
      className={cn(transferListVariants({ size, disabled }), listClassName)}
      style={{ height }}
    >
      {/* Header */}
      <div className={cn(transferListHeaderVariants({ size }))}>
        <div className="flex items-center gap-2 flex-1">
          {showSelectAll && (
            <div
              className={cn(
                transferCheckboxVariants({
                  checked: allChecked,
                  indeterminate: someChecked,
                  disabled,
                }),
                "relative flex items-center justify-center",
              )}
              onClick={handleSelectAll}
              role="checkbox"
              aria-checked={
                allChecked ? "true" : someChecked ? "mixed" : "false"
              }
              tabIndex={disabled ? -1 : 0}
            >
              {allChecked && (
                <svg
                  className="w-3 h-3 text-white"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={3}
                    d="M5 13l4 4L19 7"
                  />
                </svg>
              )}
              {someChecked && !allChecked && (
                <div className="w-2 h-0.5 bg-white" />
              )}
            </div>
          )}
          <span className="font-medium">{title}</span>
        </div>
        {showItemCount && (
          <span className="text-xs text-muted-foreground">
            {selectedKeys.length}/{items.length}
          </span>
        )}
      </div>

      {/* Search */}
      {showSearch && (
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <input
            type="text"
            value={searchValue}
            onChange={(e) => handleSearch(e.target.value)}
            placeholder={searchPlaceholder}
            className={cn(transferSearchVariants({ size }), "pl-10")}
            disabled={disabled}
          />
        </div>
      )}

      {/* Body */}
      <div className={cn(transferListBodyVariants({ size }))}>
        {filteredItems.length === 0 ? (
          <div className={cn(transferEmptyVariants({ size }))}>
            <Inbox className="h-8 w-8 mb-2 opacity-50" />
            {emptyText || "No items"}
          </div>
        ) : (
          filteredItems.map((item) => {
            const isSelected = selectedKeys.includes(item.key);
            return (
              <div
                key={item.key}
                onClick={() => handleItemClick(item)}
                className={cn(
                  transferItemVariants({
                    selected: isSelected,
                    disabled: item.disabled || disabled,
                    size,
                  }),
                  itemClassName,
                )}
                role="option"
                aria-selected={isSelected}
                tabIndex={item.disabled || disabled ? -1 : 0}
                onKeyDown={(e) => {
                  if (e.key === "Enter" || e.key === " ") {
                    e.preventDefault();
                    handleItemClick(item);
                  }
                }}
              >
                <div
                  className={cn(
                    transferCheckboxVariants({
                      checked: isSelected,
                      disabled: item.disabled || disabled,
                    }),
                    "relative flex items-center justify-center flex-shrink-0",
                  )}
                >
                  {isSelected && (
                    <svg
                      className="w-3 h-3 text-white"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={3}
                        d="M5 13l4 4L19 7"
                      />
                    </svg>
                  )}
                </div>

                {render ? (
                  render(item)
                ) : (
                  <div className="flex-1 min-w-0">
                    {item.icon && (
                      <span className="mr-2 flex-shrink-0">{item.icon}</span>
                    )}
                    <span className="truncate">{item.label}</span>
                    {item.description && (
                      <p className="text-xs text-muted-foreground truncate mt-0.5">
                        {item.description}
                      </p>
                    )}
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>

      {/* Footer */}
      {footer && (
        <div className={cn(transferFooterVariants({ size }))}>{footer}</div>
      )}
    </div>
  );
};

TransferList.displayName = "TransferList";

/**
 * Transfer Component
 *
 * A shuttle/transfer component for moving items between two lists.
 * Perfect for selecting multiple items from a large dataset.
 * Supports search, selection, and custom rendering.
 *
 * @example
 * ```tsx
 * // Basic transfer
 * const [targetKeys, setTargetKeys] = useState<string[]>([]);
 * <Transfer
 *   dataSource={items}
 *   targetKeys={targetKeys}
 *   onChange={(keys) => setTargetKeys(keys)}
 * />
 *
 * // With search
 * <Transfer
 *   dataSource={items}
 *   targetKeys={targetKeys}
 *   onChange={(keys) => setTargetKeys(keys)}
 *   showSearch
 * />
 *
 * // With custom titles
 * <Transfer
 *   dataSource={items}
 *   targetKeys={targetKeys}
 *   onChange={(keys) => setTargetKeys(keys)}
 *   titles={['Available Users', 'Selected Users']}
 * />
 *
 * // Vertical orientation
 * <Transfer
 *   dataSource={items}
 *   targetKeys={targetKeys}
 *   onChange={(keys) => setTargetKeys(keys)}
 *   orientation="vertical"
 * />
 * ```
 */
const Transfer = React.forwardRef<HTMLDivElement, TransferProps>(
  (
    {
      dataSource,
      targetKeys = [],
      selectedKeys: controlledSelectedKeys,
      onChange,
      onSelectChange,
      titles = ["Source", "Target"],
      showSearch = false,
      onSearch,
      filterOption,
      searchPlaceholder = "Search...",
      render,
      showSelectAll = true,
      footer,
      operations = [<ChevronRight key="right" />, <ChevronLeft key="left" />],
      orientation = "horizontal",
      size = "md",
      disabled = false,
      showItemCount = true,
      emptyText,
      className,
      listClassName,
      operationsClassName,
      itemClassName,
      listHeight,
      pagination = false,
      pageSize = 10,
      virtualized = false,
      ...props
    },
    ref,
  ) => {
    const [sourceSelectedKeys, setSourceSelectedKeys] = useState<string[]>([]);
    const [targetSelectedKeys, setTargetSelectedKeys] = useState<string[]>([]);

    // Split data into source and target
    const sourceItems = useMemo(
      () =>
        dataSource.filter(
          (item: TransferItem) => !targetKeys.includes(item.key),
        ),
      [dataSource, targetKeys],
    );

    const targetItems = useMemo(
      () =>
        targetKeys
          .map((key: string) =>
            dataSource.find((item: TransferItem) => item.key === key),
          )
          .filter(Boolean) as TransferItem[],
      [dataSource, targetKeys],
    );

    // Handle move to target
    const moveToTarget = () => {
      if (disabled) return;

      const newTargetKeys: string[] = [...targetKeys, ...sourceSelectedKeys];
      onChange?.(newTargetKeys, "right", sourceSelectedKeys);
      setSourceSelectedKeys([]);
    };

    // Handle move to source
    const moveToSource = () => {
      if (disabled) return;

      const newTargetKeys: string[] = targetKeys.filter(
        (key: string) => !targetSelectedKeys.includes(key),
      );
      onChange?.(newTargetKeys, "left", targetSelectedKeys);
      setTargetSelectedKeys([]);
    };

    // Handle source selection change
    const handleSourceSelectChange = (keys: string[]) => {
      setSourceSelectedKeys(keys);
      onSelectChange?.(keys, targetSelectedKeys);
