    const canMoveToTarget = sourceSelectedKeys.length > 0 && !disabled;
    const canMoveToSource = targetSelectedKeys.length > 0 && !disabled;

    return (
      <div
        ref={ref}
        className={cn(transferVariants({ orientation, size }), className)}
        {...props}
      >
        {/* Source List */}
        <TransferList
          direction="left"
