            className={cn(
              transferOperationButtonVariants({ size, orientation }),
            )}
            aria-label="Move selected items to source"
          >
            {operations[1]}
          </button>
        </div>

        {/* Target List */}
        <TransferList
          direction="right"
          title={titles[1]}
          items={targetItems}
          selectedKeys={targetSelectedKeys}
          onSelectChange={handleTargetSelectChange}
          disabled={disabled}
          showSearch={showSearch}
