/**
 * Typography Component
 *
 * A flexible typography component that renders semantic HTML elements with
 * beautiful styling and advanced customization options.
 *
 * @example
 * ```tsx
 * // Compact API
 * <Typography variant="h1">Heading 1</Typography>
 * <Typography variant="p" color="muted">Paragraph text</Typography>
 *
 * // With customization
 * <Typography
 *   variant="h2"
 *   color="primary"
 *   align="center"
 *   weight="bold"
 * >
 *   Custom Heading
 * </Typography>
 *
 * // Component API
 * <H1>Heading 1</H1>
 * <P color="muted">Paragraph</P>
 * <Lead>Lead text</Lead>
 * ```
 */
export const Typography = React.forwardRef<TypographyElement, TypographyProps>(
  (
    {
      variant = "p",
      color,
      align,
      weight,
      transform,
      decoration,
      truncate,
      as,
      className,
      children,
      ...props
    },
    ref
  ) => {
    // Determine the HTML element to render
    const getElement = (): React.ElementType => {
      if (as) return as;

      switch (variant) {
        case "h1":
        case "h2":
        case "h3":
        case "h4":
        case "h5":
        case "h6":
          return variant;
        case "blockquote":
          return "blockquote";
        case "code":
          return "code";
        case "list":
          return "ul";
        case "inlineCode":
          return "code";
        default:
          return "p";
      }
    };

    const Element = getElement();

    return React.createElement(
      Element,
      {
        ref,
        className: cn(
          typographyVariants({
            variant,
            color,
            align,
            weight,
            transform,
            decoration,
            truncate,
          }),
          className
        ),
        ...props,
      },
      children
    );
  }
);

