          onDrop={uploadType !== "button" ? handleDrop : undefined}
          className={cn(
            uploadVariants({
              variant,
              size: uploadType === "button" ? "sm" : size,
              rounded,
              uploadType,
              isDragActive,
            }),
            disabled && "pointer-events-none",
            className,
            dropzoneClassName
          )}
