              ) : (
                <div className="flex flex-col items-center gap-2 p-4">
                  <UploadIcon className="h-8 w-8 text-muted-foreground" />
                  <span className="text-sm text-muted-foreground">
                    {uploadType === "avatar" ? "Upload Avatar" : "Upload Image"}
                  </span>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Error Message */}
        {error && (
          <p className="text-sm font-medium text-destructive">{error}</p>
        )}

        {/* File List */}
        {showFileList &&
          fileList.length > 0 &&
          uploadType !== "avatar" &&
          uploadType !== "image" && (
            <div className={cn("space-y-2", fileListClassName)}>
              {fileList.map((file) => (
                <div
                  key={file.id}
                  className={cn(fileItemVariants({ status: file.status }))}
                >
                  {/* Preview Thumbnail */}
                  {showPreview && file.preview && (
                    <img
                      src={file.croppedPreview || file.preview}
                      alt={file.name}
                      className="h-12 w-12 rounded object-cover"
                    />
                  )}

                  {/* File Icon */}
                  {(!showPreview || !file.preview) && renderFileIcon(file)}

                  {/* File Info */}
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-foreground truncate">
                      {file.name}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {formatFileSize(file.size)}
                    </p>
                    {file.error && (
                      <p className="text-xs text-destructive">{file.error}</p>
                    )}
                  </div>

                  {/* Progress Bar */}
                  {file.status === "uploading" &&
                    file.progress !== undefined && (
                      <div className="flex-1 max-w-[150px]">
                        <div className="h-2 w-full rounded-full bg-muted">
                          <div
                            className="h-full rounded-full bg-primary transition-all duration-300"
                            style={{ width: `${file.progress}%` }}
                          />
                        </div>
                      </div>
                    )}

                  {/* Status */}
                  {renderFileStatus(file)}

                  {/* Actions */}
                  <div className="flex items-center gap-2">
                    {showPreview && file.preview && (
                      <button
                        type="button"
                        onClick={() => previewImage?.(file)}
                        className="text-muted-foreground hover:text-foreground transition-colors"
                        title="Preview"
                      >
                        <Eye className="h-4 w-4" />
                      </button>
                    )}
                    {enableImageEdit &&
                      file.preview &&
                      file.type.startsWith("image/") &&
                      !readOnly && (
                        <button
                          type="button"
                          onClick={() => handleEditImage(file)}
                          className="text-muted-foreground hover:text-primary transition-colors"
                          title="Edit image"
                        >
                          <Edit className="h-4 w-4" />
                        </button>
                      )}
                    {allowRemove && !readOnly && (
                      <button
                        type="button"
                        onClick={() => handleRemove(file)}
                        className="text-muted-foreground hover:text-destructive transition-colors"
                        title="Remove"
                      >
                        <X className="h-4 w-4" />
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
