        {/* Image Editor Modal */}
        {showImageEditor && editingFile && editingFile.preview && (
          <ImageEditor
            imageUrl={editingFile.preview}
            onSave={handleSaveCrop}
            onCancel={handleCancelCrop}
            aspectRatio={cropAspectRatio}
          />
        )}
      </div>
    );
  }
);

