 * @returns [formState, formActions]
 *
 * @example
 * const [form, { handleChange, handleBlur, validate }] = useForm(
 *   { email: '', password: '' },
 *   {
 *     email: (value) => !value ? 'Required' : undefined,
 *     password: (value) => value.length < 6 ? 'Min 6 chars' : undefined
